import React from 'react'
import { NavLink, Outlet } from 'react-router-dom'

const Home = () => {
  return (
    <>
       <div className="dashboard"> {/* Apply a base class for the entire dashboard */}
      <nav className="sidebar"> {/* Define a dedicated class for the sidebar */}
        <ul className="sidebar__list"> {/* Use an unordered list for clear organization */}
          <li className="sidebar__item"> {/* Define list item class for styling */}
            <NavLink to="/" className={({ isActive }) => (
              isActive ? 'sidebar__item--active' : 'sidebar__item'
            )}>
              G&G DATA CENTER, WOB
            </NavLink>
          </li>
          <li className="sidebar__item">
            <NavLink to="vrc" className={({ isActive }) => (
              isActive ? 'sidebar__item--active' : 'sidebar__item'
            )}>
              VRC, WOB
            </NavLink>
          </li>
          <li className="sidebar__item">
            <NavLink to="dr" className={({ isActive }) => (
              isActive ? 'sidebar__item--active' : 'sidebar__item'
            )}>
              DR CENTER, WOB
            </NavLink>
          </li>
          <li className="sidebar__item">
            <NavLink to="spic" className={({ isActive }) => (
              isActive ? 'sidebar__item--active' : 'sidebar__item'
            )}>
              SPIC, WOB
            </NavLink>
          </li>
        </ul>
      </nav>
      <main className="content"> {/* Define a dedicated class for the main content area */}
        <Outlet /> {/* Render child components based on routes */}
      </main>
    </div>
    </>
  )
}

export default Home
