import { Chip } from '@mui/material'
import React, { useState } from 'react'

const Badge = (props) => {

    const [open, setOpen] = useState(false)
    
    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        props.label ?
            <>
                <Chip label={props.label} onClick={handleClickOpen} />
                
            </>
            : <></>
    
    )
}

export default Badge
