import React from 'react';
import { NavLink, Outlet, useLocation } from 'react-router-dom';

const Admin = () => {
  const location = useLocation();

  const links = [
    { path: '/', label: 'G&G DATA CENTER, WOB' },
    { path: 'vrc', label: 'VRC, WOB' },
    { path: 'dr', label: 'DR CENTER, WOB' },
    { path: 'spic', label: 'SPIC, WOB' },
  ];

  const isActive = (path) => {
    // Handle exact path matching for a more robust solution
    return location.pathname === path;
  };

  return (
    <>
      <div className='w-full flex-col'>
        <div className='grid grid-rows-1 grid-cols-4 transition-all fixed w-full z-10'>
          {links.map((link) => (
            <NavLink
              key={link.path}
              to={link.path}
              className={({ isActive }) => (
                isActive(link.path)
                  ? 'flex items-center justify-center h-12 bg-amber-300 border border-red-800 border-r-0 hover:bg-amber-400 duration-300'
                  : 'flex items-center justify-center h-12 bg-slate-100 border border-red-800 border-r-0 hover:bg-slate-200 duration-300'
              )}
            >
              {link.label}
            </NavLink>
          ))}
        </div>
        <div className='pt-20'>
          <Outlet />
        </div>
      </div>
    </>
  );
};

export default Admin;
