import React from 'react'
import "./Table.css"


const Table = (props) => {

    // console.log(props);

    function addSerialNumbers() {
        const tableBody = document.querySelector('tbody');
        let serialNumber = 1; // Starting serial number
      
        // Loop through all table rows
        const tableRows = tableBody.querySelectorAll('tr');
        for (const row of tableRows) {
          // Loop through all cells in the current row
          const tableCells = row.querySelectorAll('td');
          for (const cell of tableCells) {
            cell.textContent = serialNumber + '. ' + cell.textContent;
            serialNumber++; // Increment serial number for the next cell
          }
        }
      }
      
      addSerialNumbers();

    return (
        <table className='w-full table-fixed text-center'>

            <thead className='text-3xl'>
                <tr>
                    <th>{props.name} Rack</th>
                </tr>
            </thead>
            <tbody>
                {props.rack?.map((item) => {
                    return (
                        <>
                            <tr className=''>
                                <td className=' h-10'>{item}</td>
                            </tr>
                        </>
                    )
                })}


            </tbody>
        </table>
    )
}

export default Table
