import { Button, Chip, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, TextField } from '@mui/material'
import React, { useState, useEffect } from 'react'

const Badge = (props) => {
    const [open, setOpen] = useState(false);
    const [isAdmin, setIsAdmin] = useState(false);
    const [isEditing, setIsEditing] = useState(false);
    const [editedComponent, setEditedComponent] = useState({});
    const [isChanged, setIsChanged] = useState(false);

    useEffect(() => {
        setIsAdmin(localStorage.getItem('isAdmin') === 'true');
    }, []);

    const handleClickOpen = () => {
        setOpen(true);
        setEditedComponent(props.componentDetails);
    };

    const handleClose = () => {
        setOpen(false);
        setIsEditing(false);
        setIsChanged(false);
    };

    const handleEdit = () => {
        setIsEditing(true);
    };

    const handleSave = () => {
        // Here you would typically save the changes to the backend
        console.log('Saving changes:', editedComponent);
        setIsEditing(false);
        setIsChanged(false);
        // Update the original component details
        props.onComponentUpdate(editedComponent);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setEditedComponent(prev => {
            const updated = { ...prev, [name]: value };
            setIsChanged(JSON.stringify(updated) !== JSON.stringify(props.componentDetails));
            return updated;
        });
    };

    const renderField = (label, value, name) => {
        return isEditing ? (
            <TextField
                name={name}
                value={value}
                onChange={handleInputChange}
                fullWidth
                margin="dense"
            />
        ) : (
            <DialogContentText>{value}</DialogContentText>
        );
    };

    let component = isEditing ? editedComponent : props.componentDetails;

    return (
        props.label ?
            <>
                <Chip
                    label={props.label}
                    onClick={handleClickOpen}
                    sx={{ backgroundColor: props.color, color: 'white', fontFamily: 'Montserrat', zIndex: 10 }}
                />
                <Dialog
                    open={open}
                    onClose={handleClose}
                    aria-labelledby="alert-dialog-title"
                    aria-describedby="alert-dialog-description"
                >
                    <DialogTitle id="alert-dialog-title">
                        {"Specifications"}
                    </DialogTitle>
                    <div className='flex w-full'>
                        <DialogContent className=''>
                            <DialogContentText id="service-tag-1">
                                Service Tag / SN
                            </DialogContentText>
                            <DialogContentText id="make-2">
                                Make / Model
                            </DialogContentText>
                            {/* ... (rest of the fields) ... */}
                        </DialogContent>
                        <DialogContent>
                            <DialogContentText>:</DialogContentText>
                            <DialogContentText>:</DialogContentText>
                            {/* ... (rest of the separators) ... */}
                        </DialogContent>
                        <DialogContent>
                            {renderField("Serial Number", component.specifications.serialNo, "serialNo")}
                            {renderField("Make/Model", component.specifications.makeModel, "makeModel")}
                            {/* ... (render other fields based on component type) ... */}
                            {renderField("Vendor", `${component.specifications?.vendorID.vendorID} (${component.specifications?.vendorID.vendorName})`, "vendor")}
                        </DialogContent>
                    </div>
                    <DialogActions>
                        {isAdmin && !isEditing && (
                            <Button onClick={handleEdit}>Edit</Button>
                        )}
                        {isAdmin && isEditing && isChanged && (
                            <Button onClick={handleSave}>Save</Button>
                        )}
                        <Button onClick={handleClose}>Close</Button>
                    </DialogActions>
                </Dialog>
            </>
            : <></>
    )
}

export default Badge
