import React from 'react';
import './Table.css';
import Badge from './Badge';

const Table = ({ rack, name }) => {
    const TOTAL_SLOTS = 20;

    const createSlotArray = (data) => {
        let slotArray = new Array(TOTAL_SLOTS).fill(null);
        
        data.forEach(item => {
            for (let i = item.startSlot; i < item.startSlot + item.size; i++) {
                slotArray[i - 1] = {
                    ...item,
                    isStart: i === item.startSlot,
                    isEnd: i === item.startSlot + item.size - 1
                };
            }
        });

        return slotArray;
    };

    const slotArray = createSlotArray(rack);

    return (
        <table className="w-full table-fixed text-center text-white bg-teal-600 -z-0">
            <thead className="text-3xl">
                <tr>
                    <th className="py-2">{name} Rack</th>
                </tr>
            </thead>
            <tbody>
                {slotArray.map((item, index) => {
                    const slotNumber = index + 1;
                    return (
                        <tr key={index} className={`${item?.isStart ? 'border-t-2' : ''} ${item?.isEnd ? 'border-b-2' : ''} border-white`}>
                            <td className="h-12 relative">
                                <div className="absolute inset-0 flex items-center">
                                    <span className="pl-2 absolute left-0">
                                        {slotNumber}.
                                    </span>
                                    <div className="ml-4 w-full flex justify-center items-center">
                                        <span className="w-10/12">
                                            {item ? (
                                                <Badge label={item.name} color={item.color} />
                                            ) : (
                                                <div className="h-full w-full bg-transparent" />
                                            )}
                                        </span>
                                    </div>
                                </div>
                            </td>
                        </tr>
                    );
                })}
            </tbody>
        </table>
    );
};

export default Table;
