import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  Box, Container, Typography, Grid, Paper, Select, MenuItem, FormControl, InputLabel,
  Card, CardContent, Chip, Accordion, AccordionSummary, AccordionDetails, LinearProgress,
  Button, TextField, AppBar, Toolbar
} from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import StorageIcon from '@mui/icons-material/Storage';
import MemoryIcon from '@mui/icons-material/Memory';
import RouterIcon from '@mui/icons-material/Router';
import BackupIcon from '@mui/icons-material/Backup';
import DevicesOtherIcon from '@mui/icons-material/DevicesOther';
import SearchIcon from '@mui/icons-material/Search';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
    background: {
      default: '#f5f5f5',
    },
  },
});

const componentIcons = {
  1: <StorageIcon />,
  2: <MemoryIcon />,
  3: <RouterIcon />,
  4: <BackupIcon />,
  5: <DevicesOtherIcon />,
};

const Report = () => {
  const [components, setComponents] = useState({});
  const [filteredComponents, setFilteredComponents] = useState({});
  const [dataCenters, setDataCenters] = useState([]);
  const [vendors, setVendors] = useState([]);
  const [filters, setFilters] = useState({
    type: '',
    vendor: '',
    dataCenter: '',
    search: '',
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    // ... (keep this function as it was)
  };

  const applyFilters = () => {
    const filtered = Object.entries(components).reduce((acc, [rackName, rackComponents]) => {
      const componentsArray = Array.isArray(rackComponents) ? rackComponents : [];
      
      const filteredRackComponents = componentsArray.filter(component => {
        return (
          (!filters.type || component.type === filters.type) &&
          (!filters.vendor || component.vendor === filters.vendor) &&
          (!filters.dataCenter || component.dataCenter === filters.dataCenter) &&
          (!filters.search || component.name.toLowerCase().includes(filters.search.toLowerCase()))
        );
      });
  
      if (filteredRackComponents.length > 0) {
        acc[rackName] = filteredRackComponents;
      }
  
      return acc;
    }, {});
  
    setFilteredComponents(filtered);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const handleSearch = () => {
    applyFilters();
  };

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              Component Dashboard
            </Typography>
          </Toolbar>
        </AppBar>
        <Container maxWidth="lg" sx={{ mt: 4 }}>
          <Paper elevation={3} sx={{ p: 3, mb: 4 }}>
            <Typography variant="h5" component="h2" gutterBottom>
              Filters
            </Typography>
            <Grid container spacing={2} alignItems="flex-end">
              <Grid item xs={12} sm={6} md={3}>
                <FormControl fullWidth>
                  <InputLabel>Type</InputLabel>
                  <Select
                    name="type"
                    value={filters.type}
                    onChange={handleFilterChange}
                    label="Type"
                  >
                    <MenuItem value="">All Types</MenuItem>
                    {Object.entries(componentTypes).map(([key, value]) => (
                      <MenuItem key={key} value={key}>
                        {componentIcons[key]} {value}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={6} md={3}>
                <FormControl fullWidth>
                  <InputLabel>Vendor</InputLabel>
                  <Select
                    name="vendor"
                    value={filters.vendor}
                    onChange={handleFilterChange}
                    label="Vendor"
                  >
                    <MenuItem value="">All Vendors</MenuItem>
                    {vendors.map(vendor => (
                      <MenuItem key={vendor._id} value={vendor._id}>{vendor.vendorName}</MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={6} md={3}>
                <FormControl fullWidth>
                  <InputLabel>Data Center</InputLabel>
                  <Select
                    name="dataCenter"
                    value={filters.dataCenter}
                    onChange={handleFilterChange}
                    label="Data Center"
                  >
                    <MenuItem value="">All Data Centers</MenuItem>
                    {dataCenters.map(dc => (
                      <MenuItem key={dc._id} value={dc._id}>{dc.name}</MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={6} md={3}>
                <TextField
                  fullWidth
                  name="search"
                  label="Search Components"
                  value={filters.search}
                  onChange={handleFilterChange}
                />
              </Grid>
              <Grid item xs={12}>
                <Button
                  variant="contained"
                  color="primary"
                  startIcon={<SearchIcon />}
                  onClick={handleSearch}
                  fullWidth
                >
                  Search
                </Button>
              </Grid>
            </Grid>
          </Paper>

          {loading ? (
            <LinearProgress />
          ) : (
            Object.entries(filteredComponents).map(([rackName, rackComponents]) => (
              <Accordion key={rackName} defaultExpanded sx={{ mb: 2 }}>
                <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                  <Typography variant="h6">{rackName}</Typography>
                </AccordionSummary>
                <AccordionDetails>
                  <Grid container spacing={2}>
                    {rackComponents.sort((a, b) => a.startSlot - b.startSlot).map(component => (
                      <Grid item xs={12} sm={6} md={4} key={component._id}>
                        <Card elevation={3}>
                          <CardContent>
                            <Typography variant="h6" component="h2" gutterBottom>
                              {component.name}
                            </Typography>
                            <Box display="flex" alignItems="center" mb={1}>
                              {componentIcons[component.type]}
                              <Chip 
                                label={componentTypes[component.type] || component.type} 
                                color="primary" 
                                size="small" 
                                sx={{ ml: 1 }}
                              />
                            </Box>
                            <Typography variant="body2" color="text.secondary">
                              Slot: {component.startSlot}
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                              Slots Occupied: {component.size}
                            </Typography>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                </AccordionDetails>
              </Accordion>
            ))
          )}
        </Container>
      </Box>
    </ThemeProvider>
  );
};

export default Report;
