import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TOTAL_SLOTS } from '../reusable/Table';

const GnG_Admin = ({ dataCenter }) => {
    const [name, setName ] = useState("")
    const [components, setComponents] = useState({});
    const [stats, setStats] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isDialogOpen, setIsDialogOpen] = useState(false);
    const [newComponent, setNewComponent] = useState({
        type: '',
        name: '',
        startSlot: '',
        rack: '',
        size: 1,
    });

    const updateComponents = (data) => {
        setComponents(c => ({ ...c, ...data }))
    }

    const [availableSlots, setAvailableSlots] = useState([]);

    const componentTypes = {
        1: 'server',
        2: 'storage',
        3: 'switch',
        4: 'tape drive',
        5: 'others'
    };
    const rackTypes = ['Server', 'Network1', 'Network2', 'Storage', 'Peripheral'];

    const vendorList = {
        100001: 'Dell',
        100002: 'Cisco',
        100004: 'NeoSeries',
    };

    useEffect(() => {
        fetchComponents();
        

        switch (dataCenter) {
            case 1:
                setName('G&G Data Center');
                break;
            case 2:
                setName('VRC');
                break;
            case 3:
                setName('DR');
                break;
            case 4:
                setName('SPIC');
                break;
            default:
                setName('Unknown Data Center');
                break;
        }

    }, [dataCenter]);

    const fetchComponents = async () => {
        try {
            setLoading(true);
            const response = await axios.get(`/general/data-centers/${dataCenter}/components`);

            updateComponents(response.data);
            calculateStats(response.data);
            setLoading(false);
        } catch (err) {
            setError('Error fetching components');
            setLoading(false);
        }
    };

    const calculateStats = (data) => {
        const stats = {
            totalComponents: 0,
            componentsByType: {},
            componentsByRack: {},
        };

        Object.values(data).forEach(rackComponents => {
            rackComponents.forEach(component => {
                stats.totalComponents++;
                stats.componentsByType[component.type] = (stats.componentsByType[component.type] || 0) + 1;
                stats.componentsByRack[component.rackId.name] = (stats.componentsByRack[component.rackId.name] || 0) + 1;
            });
        });

        setStats(stats);
    };
    useEffect(() => {
        if (newComponent.size && newComponent.rack) {
            calculateAvailableSlots(newComponent.rack, components);
        }
    }, [newComponent.size, newComponent.rack, components]);

    const calculateAvailableSlots = (rack, data) => {
        const occupiedSlots = new Set();

        // Check if the rack exists in the data object
        if (data[rack]) {
            // Iterate through the components in the specified rack
            data[rack].forEach(component => {
                // Adjust the occupied slots based on slotsOccupied and startSlot
                for (let i = component.startSlot; i < component.startSlot + component.size; i++) {
                    occupiedSlots.add(i);
                }
            });
        }

        // Calculate available slots
        const availableSlots = Array.from({ length: TOTAL_SLOTS }, (_, i) => i + 1).filter(slot => {
            // Check if the slot is available for the desired slotsOccupied
            for (let j = 0; j < newComponent.size; j++) {
                if (occupiedSlots.has(slot + j)) {
                    return false; // Slot is occupied, so exclude it
                }
            }
            return true; // Slot is available
        });

        setAvailableSlots(availableSlots);
        console.log(availableSlots)
    };


    const handleDelete = async (componentId) => {
        if (window.confirm('Are you sure you want to delete this component?')) {
            try {
                await axios.delete(`/general/components/${componentId}`);
                fetchComponents(); // Refresh the list
            } catch (err) {
                setError('Error deleting component');
            }
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewComponent(prev => ({ ...prev, [name]: value }));
        if (name === 'rack') {
            calculateAvailableSlots(value, components);
        }
    };


    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('/general/components', newComponent);
            setIsDialogOpen(false);
            setNewComponent({ type: '', name: '', startSlot: '', rack: '', size: 1 });
            fetchComponents(); // Refresh the list
        } catch (err) {
            setError('Error adding component');
        }
    };

    const handleExtend = async (componentId, currentSlots) => {
        const newSlots = prompt(`Enter new number of slots (current: ${currentSlots}):`, currentSlots);
        if (newSlots && !isNaN(newSlots)) {
            try {
                await axios.put(`/general/components/${componentId}`, { size: parseInt(newSlots) });
                fetchComponents(); // Refresh the list
            } catch (err) {
                setError('Error extending component');
            }
        }
    };

    const renderComponentFields = () => {
        switch (newComponent.type) {
            case '1': // Server
                return (
                    <>
                        <input
                            type="text"
                            name="serviceTag"
                            value={newComponent.serviceTag || ''}
                            onChange={handleInputChange}
                            placeholder="Service Tag / SN"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <input
                            type="text"
                            name="makeModel"
                            value={newComponent.makeModel || ''}
                            onChange={handleInputChange}
                            placeholder="Make / Model"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <input
                            type="number"
                            name="ram"
                            value={newComponent.ram || ''}
                            onChange={handleInputChange}
                            placeholder="RAM (GB)"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <input
                            type="number"
                            name="hdd"
                            value={newComponent.hdd || ''}
                            onChange={handleInputChange}
                            placeholder="HDD (GB)"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <input
                            type="text"
                            name="processor"
                            value={newComponent.processor || ''}
                            onChange={handleInputChange}
                            placeholder="Processor"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <select
                            name="vendor"
                            value={newComponent.vendor || ''}
                            onChange={handleInputChange}
                            className="w-full p-2 mb-4 border rounded"
                            required
                        >
                            <option value="">Select Vendor</option>
                            {Object.entries(vendorList).map(([id, name]) => (
                                <option key={id} value={id}>{name}</option>
                            ))}
                        </select>
                    </>
                );
            case '2': // Storage
                return (
                    <>
                        <input
                            type="text"
                            name="serviceTag"
                            value={newComponent.serviceTag || ''}
                            onChange={handleInputChange}
                            placeholder="Service Tag / SN"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <input
                            type="text"
                            name="makeModel"
                            value={newComponent.makeModel || ''}
                            onChange={handleInputChange}
                            placeholder="Make / Model"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <select
                            name="vendor"
                            value={newComponent.vendor || ''}
                            onChange={handleInputChange}
                            className="w-full p-2 mb-4 border rounded"
                            required
                        >
                            <option value="">Select Vendor</option>
                            {Object.entries(vendorList).map(([id, name]) => (
                                <option key={id} value={id}>{name}</option>
                            ))}
                        </select>
                    </>
                );
            case '3': // Switch
                return (
                    <>
                        <input
                            type="text"
                            name="serviceTag"
                            value={newComponent.serviceTag || ''}
                            onChange={handleInputChange}
                            placeholder="Service Tag / SN"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <input
                            type="text"
                            name="makeModel"
                            value={newComponent.makeModel || ''}
                            onChange={handleInputChange}
                            placeholder="Make / Model"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <input
                            type="number"
                            name="ports"
                            value={newComponent.ports || ''}
                            onChange={handleInputChange}
                            placeholder="No. of ports"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <select
                            name="vendor"
                            value={newComponent.vendor || ''}
                            onChange={handleInputChange}
                            className="w-full p-2 mb-4 border rounded"
                            required
                        >
                            <option value="">Select Vendor</option>
                            {Object.entries(vendorList).map(([id, name]) => (
                                <option key={id} value={id}>{name}</option>
                            ))}
                        </select>
                    </>
                );
            case '4': // Tape Drive
                return (
                    <>
                        <input
                            type="text"
                            name="serviceTag"
                            value={newComponent.serviceTag || ''}
                            onChange={handleInputChange}
                            placeholder="Service Tag / SN"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <input
                            type="text"
                            name="makeModel"
                            value={newComponent.makeModel || ''}
                            onChange={handleInputChange}
                            placeholder="Make / Model"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <input
                            type="number"
                            name="drives"
                            value={newComponent.drives || ''}
                            onChange={handleInputChange}
                            placeholder="No. of drives"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <input
                            type="number"
                            name="slots"
                            value={newComponent.slots || ''}
                            onChange={handleInputChange}
                            placeholder="No. of slots"
                            className="w-full p-2 mb-4 border rounded"
                            required
                        />
                        <select
                            name="vendor"
                            value={newComponent.vendor || ''}
                            onChange={handleInputChange}
                            className="w-full p-2 mb-4 border rounded"
                            required
                        >
                            <option value="">Select Vendor</option>
                            {Object.entries(vendorList).map(([id, name]) => (
                                <option key={id} value={id}>{name}</option>
                            ))}
                        </select>
                    </>
                );
            default:
                return null;
        }
    };

    const renderAvailableSlots = () => {
        return (
            <select
                name="startSlot"
                value={newComponent.startSlot}
                onChange={handleInputChange}
                className="w-full p-2 mb-4 border rounded"
                required
            >
                <option value="">Select Start Slot</option>
                {availableSlots.map(slot => (
                    <option key={slot} value={slot}>
                        {slot}
                    </option>
                ))}
            </select>
        );
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4">Data Center Admin - {name}</h1>

            <button
                onClick={() => setIsDialogOpen(true)}
                className="bg-blue-500 text-white px-4 py-2 rounded mb-4"
            >
                Add New Component
            </button>

            {/* Insert Component Dialog */}
            {isDialogOpen && (
                <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full">
                    <div className=" relative top-32 mx-auto p-5 border w-10/12 shadow-lg rounded-md bg-white">
                        <h3 className="text-lg font-semibold mb-4">Add New Component</h3>
                        <form onSubmit={handleSubmit}>
                            <div className=' grid grid-cols-2 gap-3'>

                                <select
                                    name="type"
                                    value={newComponent.type}
                                    onChange={handleInputChange}
                                    className="w-full p-2 mb-4 border rounded"
                                    required
                                >
                                    <option value="">Select Component Type</option>
                                    {Object.entries(componentTypes).map(([key, value]) => (
                                        <option key={key} value={key}>{value}</option>
                                    ))}
                                </select>
                                {renderComponentFields()}
                                <input
                                    type="text"
                                    name="name"
                                    value={newComponent.name}
                                    onChange={handleInputChange}
                                    placeholder="Component Name"
                                    className="w-full p-2 mb-4 border rounded"
                                    required
                                />
                                <select
                                    name="rack"
                                    value={newComponent.rack}
                                    onChange={handleInputChange}
                                    className="w-full p-2 mb-4 border rounded"
                                    required
                                >
                                    <option value="">Select Rack</option>
                                    {rackTypes.map((rack, index) => (
                                        <option key={index} value={rack}>{rack}</option>
                                    ))}
                                </select>
                                <select
                                    name="size"
                                    value={newComponent.size}
                                    onChange={handleInputChange}
                                    className="w-full p-2 mb-4 border rounded"
                                    required
                                >
                                    <option value="">Select Slots Occupied</option>
                                    {Array.from({ length: 40 - newComponent.startSlot + 1 }, (_, i) => i + 1).map((slot) => (
                                        <option key={slot} value={slot}>{slot}</option>
                                    ))}
                                </select>
                                {renderAvailableSlots()}
                                {/* <select
                  name="startSlot"
                  value={newComponent.startSlot}
                  onChange={handleInputChange}
                  className="w-full p-2 mb-4 border rounded"
                  required
                >
                  <option value="">Select Start Slot</option>
                  {availableSlots.map((slot) => (
                    <option key={slot} value={slot}>{slot}</option>
                  ))}
                </select> */}
                            </div>
                            <div className="flex justify-end">
                                <button
                                    type="button"
                                    onClick={() => setIsDialogOpen(false)}
                                    className="bg-gray-300 text-black px-4 py-2 rounded mr-2"
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    className="bg-blue-500 text-white px-4 py-2 rounded"
                                >
                                    Add Component
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

<div className="mb-8">
      <h2 className="text-xl font-semibold mb-2">Statistics</h2>
      <p>Total Components: {stats.totalComponents}</p>
      <h3 className="text-lg font-semibold mt-2">Components by Type:</h3>
      <ul>
        {Object.entries(stats.componentsByType).map(([type, count]) => (
          <li key={type}>
            {componentTypes[type].toUpperCase()}: {count}
          </li>
        ))}
      </ul>
      <h3 className="text-lg font-semibold mt-2">Components by Rack:</h3>
      <ul>
        {Object.entries(stats.componentsByRack).map(([rack, count]) => (
          <li key={rack}>
            {rack}: {count}
          </li>
        ))}
      </ul>
    </div>

            <div>
                <h2 className="text-xl font-semibold mb-2">Component List</h2>
                {Object.entries(components).map(([rackName, rackComponents]) => (
                    <div key={rackName} className="mb-4">
                        <h3 className="text-lg font-semibold">{rackName}</h3>
                        <ul>
                            {rackComponents.map(component => (
                                <li key={component._id} className="flex justify-between items-center mb-2">
                                    <span>
                                        {component.name} (Type: {componentTypes[component.type] || component.type},
                                        Slot: {component.startSlot}, Slots Occupied: {component.size})
                                    </span>
                                    <div>
                                        <button
                                            onClick={() => handleExtend(component._id, component.size)}
                                            className="bg-yellow-500 text-white px-2 py-1 rounded mr-2"
                                        >
                                            Extend/Retract
                                        </button>
                                        <button
                                            onClick={() => handleDelete(component._id)}
                                            className="bg-red-500 text-white px-2 py-1 rounded"
                                        >
                                            Delete
                                        </button>
                                    </div>
                                </li>
                            ))}
                        </ul>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default GnG_Admin;
