import { Button, Chip, Dialog, DialogActions, DialogContent, DialogTitle, Grid, MenuItem, Select, TextField, Typography } from '@mui/material';
import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { FaArrowRight } from 'react-icons/fa';

const Badge = (props) => {
  const [open, setOpen] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editedComponent, setEditedComponent] = useState({});
  const [isChanged, setIsChanged] = useState(false);
  const [showChanges, setShowChanges] = useState(false);
  const [vendors, setVendors] = useState([]);

  const [vendorValue, setVendorValue] = useState(`${props.componentDetails.specifications.vendorID.vendorID}(${props.componentDetails.specifications.vendorID.vendorName})`);

  useEffect(() => {
    setIsAdmin(localStorage.getItem('isAdmin') === 'true');
    fetchVendors();
  }, []);

  const fetchVendors = async () => {
    try {
      const response = await axios.get('/general/vendors');
      setVendors(response.data);
    } catch (error) {
      console.error('Error fetching vendors:', error);
    }
  };

  const handleClickOpen = () => {
    setOpen(true);
    setEditedComponent(props.componentDetails);
  };

  const handleClose = () => {
    setOpen(false);
    setIsEditing(false);
    setIsChanged(false);
  };

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    setShowChanges(true);
  };

  const confirmSave = async () => {
    try {
      setIsEditing(false);
      setIsChanged(false);
      setShowChanges(false);

      console.log("Old details: ", props.componentDetails);
      console.log("Sending new component details: ", editedComponent);
      editedComponent.specifications.vendorID = editedComponent.specifications.vendorID._id;

      const response = await axios.put(`/admin/edit-component/${editedComponent._id}`, {
        specifications: editedComponent.specifications,
        customFields: editedComponent.customFields
      });

      if (response.status === 200) {
        console.log('Component updated successfully');
      } else {
        throw new Error('Failed to update component');
      }

      handleClose();
    } catch (error) {
      console.error('Error updating component:', error);
    }
    props.onSave();
  };

  const handleInputChange = (key, value, vendorId = null) => {
    setEditedComponent(prev => {
      const updated = {
        ...prev,
        specifications: {
          ...prev.specifications,
        },
        customFields: [...prev.customFields]
      };

      if (vendorId) {
        const matches = value.match(/^(.+?)\((.+?)\)$/);
        if (matches) {
          setVendorValue(value);
          updated.specifications.vendorID = {
            _id: vendorId,
            vendorName: matches[2],
            vendorID: matches[1],
          };
        }
      } else {
        const originalValue = props.componentDetails.specifications[key];
        let typedValue = value;

        if (typeof originalValue === 'number' && !isNaN(Number(value))) {
          typedValue = Number(value);
        } else if (typeof originalValue === 'boolean') {
          typedValue = value === 'true' || value === true;
        }

        updated.specifications[key] = typedValue;
      }

      const hasChanged = isObjectChanged(updated, props.componentDetails);
      setIsChanged(hasChanged);

      return updated;
    });
  };

  const handleCustomFieldChange = (index, value) => {
    setEditedComponent(prev => {
      const updatedCustomFields = [...prev.customFields];
      updatedCustomFields[index].value = value;

      const updated = {
        ...prev,
        customFields: updatedCustomFields
      };

      const hasChanged = isObjectChanged(updated, props.componentDetails);
      setIsChanged(hasChanged);

      return updated;
    });
  };

  const isObjectChanged = (obj1, obj2) => {
    const keys1 = Object.keys(obj1);
    const keys2 = Object.keys(obj2);

    if (keys1.length !== keys2.length) {
      return true;
    }

    for (const key of keys1) {
      const val1 = obj1[key];
      const val2 = obj2[key];
      const areObjects = isObject(val1) && isObject(val2);

      if (
        (areObjects && isObjectChanged(val1, val2)) ||
        (!areObjects && val1 !== val2)
      ) {
        return true;
      }
    }

    return false;
  };

  const isObject = (object) => {
    return object != null && typeof object === 'object';
  };

  const renderComparisonField = (label, oldValue, newValue, key) => (
    <React.Fragment key={key}>
      {console.log(label)}
      <Grid item xs={4}>
        <Typography variant="subtitle2" color="text.secondary">{label}</Typography>
      </Grid>
      <Grid item xs={3}>
        <Typography variant="body2">{oldValue}</Typography>
      </Grid>
      <Grid item xs={1}>
        <FaArrowRight />
      </Grid>
      <Grid item xs={4}>
        <Typography variant="body2" color="primary" textAlign={'center'}>{newValue}</Typography>
      </Grid>
    </React.Fragment>
  );

  const renderField = (item) => {
    if (isEditing) {
      if (item.key === 'vendor') {
        return (
          <Select
            fullWidth
            value={vendorValue}
          >
            {vendors.map((vendor) => {
              const vendor_value = `${vendor.vendorID}(${vendor.vendorName})`;
              return (
                <MenuItem 
                  key={vendor._id} 
                  value={vendor_value}
                  onClick={() => handleInputChange(item.key, vendor_value, vendor._id)}
                >
                  {vendor_value}
                </MenuItem>
              );
            })}
          </Select>
        );
      } else if (item.key === 'ram' || item.key === 'hdd') {
        return (
          <TextField
            fullWidth
            variant="outlined"
            size="small"
            type="number"
            value={editedComponent.specifications[item.key]}
            onChange={(e) => handleInputChange(item.key, e.target.value)}
          />
        );
      } else {
        return (
          <TextField
            fullWidth
            variant="outlined"
            size="small"
            value={editedComponent.specifications[item.key]}
            onChange={(e) => handleInputChange(item.key, e.target.value)}
          />
        );
      }
    } else {
      if (item.key === 'vendor') {
        return <Typography variant="body2">{`${item.value.vendorID}(${item.value.vendorName})`}</Typography>;
      }
      return <Typography variant="body2">{item.value}</Typography>;
    }
  };

  const renderCustomField = (field, index) => {
    if (isEditing) {
      if (field.type === 'string') {
        return (
          <TextField
            fullWidth
            variant="outlined"
            size="small"
            value={field.value}
            onChange={(e) => handleCustomFieldChange(index, e.target.value)}
          />
        );
      } else if (field.type === 'date') {
        return (
          <TextField
            fullWidth
            variant="outlined"
            size="small"
            type="date"
            value={field.value}
            onChange={(e) => handleCustomFieldChange(index, e.target.value)}
          />
        );
      }
    } else {
      return <Typography variant="body2">{field.value}</Typography>;
    }
  };

  let component = isEditing ? editedComponent : props.componentDetails;

  return (
    props.label ?
      <>
        <Chip
          label={props.label}
          onClick={handleClickOpen}
          sx={{ backgroundColor: props.color, color: 'white', fontFamily: 'Montserrat', zIndex: 10 }}
        />
        <Dialog
          open={open}
          onClose={handleClose}
          aria-labelledby="specifications-dialog-title"
          maxWidth="sm"
          fullWidth
        >
          <DialogTitle id="specifications-dialog-title" sx={{ bgcolor: props.color, color: 'white' }}>
            <Typography variant="h6">{props.label}</Typography>
          </DialogTitle>
          <DialogContent dividers>
            {showChanges ? (
              <Grid container spacing={3}>
                {[
                  { label: "Service Tag / SN", oldValue: props.componentDetails.specifications.serialNo, newValue: editedComponent.specifications.serialNo, key: "serialNo" },
                  { label: "Make / Model", oldValue: props.componentDetails.specifications.makeModel, newValue: editedComponent.specifications.makeModel, key: "makeModel" },
                  ...(props.componentDetails.type === 1 ? [
                    { label: "RAM (GB)", oldValue: props.componentDetails.specifications.ram, newValue: editedComponent.specifications.ram, key: "ram" },
                    { label: "HDD (GB)", oldValue: props.componentDetails.specifications.hdd, newValue: editedComponent.specifications.hdd, key: "hdd" },
                  ] : []),
                  { label: "Vendor", oldValue: `${props.componentDetails.specifications.vendorID.vendorID}(${props.componentDetails.specifications.vendorID.vendorName})`, newValue: `${editedComponent.specifications.vendorID.vendorID}(${editedComponent.specifications.vendorID.vendorName})`, key: "vendor" },
                ].map(item => renderComparisonField(item.label, item.oldValue, item.newValue, item.key))}
                {editedComponent.customFields.map((field, index) => renderComparisonField(field.label, props.componentDetails.customFields[index].value, field.value, `customField_${index}`))}
              </Grid>
            ) : (
              <Grid container spacing={3}>
                {[
                  { label: "Service Tag / SN", value: component.specifications.serialNo, key: "serialNo" },
                  { label: "Make / Model", value: component.specifications.makeModel, key: "makeModel" },
                  ...(props.componentDetails.type === 1 ? [
                    { label: "RAM (GB)", value: component.specifications.ram, key: "ram" },
                    { label: "HDD (GB)", value: component.specifications.hdd, key: "hdd" },
                  ] : []),
                  { label: "Vendor", value: component.specifications.vendorID, key: "vendor" },
                ].map(item => (
                  <React.Fragment key={item.key}>
                    <Grid item xs={4}>
                      <Typography variant="subtitle2" color="text.secondary">{item.label}</Typography>
                    </Grid>
                    <Grid item xs={8}>
                      {renderField(item)}
                    </Grid>
                  </React.Fragment>
                ))}
                {component.customFields.map((field, index) => (
                  <React.Fragment key={`customField_${index}`}>
                    <Grid item xs={4}>
                      {console.log("Custom Field: ",field)}
                      <Typography variant="subtitle2" color="text.secondary">{field.name}</Typography>
                    </Grid>
                    <Grid item xs={8}>
                      {renderCustomField(field, index)}
                    </Grid>
                  </React.Fragment>
                ))}
              </Grid>
            )}
          </DialogContent>
          <DialogActions>
            {isChanged && (
              <Button onClick={handleSave} color="primary" variant="contained">
                Save Changes
              </Button>
            )}
            {showChanges && (
              <>
                <Button onClick={handleClose} color="primary">
                  Cancel
                </Button>
                <Button onClick={confirmSave} color="primary" variant="contained">
                  Confirm Save
                </Button>
              </>
            )}
            {!showChanges && !isEditing && isAdmin && (
              <Button onClick={handleEdit} color="primary" variant="contained">
                Edit
              </Button>
            )}
            <Button onClick={handleClose} color="primary">
              Close
            </Button>
          </DialogActions>
        </Dialog>
      </>
      : <></>
  );
};

export default Badge;
