import { Button, Chip, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Grid, TextField, Typography } from '@mui/material'
import React, { useState, useEffect } from 'react'

const Badge = (props) => {
  const [open, setOpen] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editedComponent, setEditedComponent] = useState({});
  const [isChanged, setIsChanged] = useState(false);
  const [showChanges, setShowChanges] = useState(false);



  useEffect(() => {
    setIsAdmin(localStorage.getItem('isAdmin') === 'true');
  }, []);

  const handleClickOpen = () => {
    setOpen(true);
    setEditedComponent(props.componentDetails);
  };

  const handleClose = () => {
    setOpen(false);
    setIsEditing(false);
    setIsChanged(false);
  };

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    setShowChanges(true);
  };

  const confirmSave = () => {
    console.log('Saving changes:', editedComponent);
    setIsEditing(false);
    setIsChanged(false);
    setShowChanges(false);
    props.onComponentUpdate(editedComponent);
    handleClose();
  };

  const renderComparisonField = (label, oldValue, newValue, key) => (
    <React.Fragment key={key}>
        <Grid item xs={4}>
            <Typography variant="subtitle2" color="text.secondary">{label}</Typography>
        </Grid>
        <Grid item xs={3}>
            <Typography variant="body2">{oldValue}</Typography>
        </Grid>
        <Grid item xs={1}>
            <FaArrowRight />
        </Grid>
        <Grid item xs={4}>
            <Typography variant="body2" color="primary">{newValue}</Typography>
        </Grid>
    </React.Fragment>
);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedComponent(prev => {
      const updated = {
        ...prev,
        specifications: {
          ...prev.specifications,
          [name]: value
        }
      };

      // Special handling for vendor
      if (name === 'vendor') {
        const [vendorID, vendorName] = value.split('(');
        updated.specifications.vendorID = {
          vendorID: vendorID.trim(),
          vendorName: vendorName.replace(')', '').trim()
        };
      }

      setIsChanged(JSON.stringify(updated) !== JSON.stringify(props.componentDetails));
      return updated;
    });
  };

  const renderField = (label, value, name) => {
    return isEditing ? (
      <TextField
        name={name}
        value={value}
        onChange={handleInputChange}
        fullWidth
        margin="dense"
      />
    ) : (
      <DialogContentText>{value}</DialogContentText>
    );
  };

  let component = isEditing ? editedComponent : props.componentDetails;


  return (
    props.label ?
      <>
        <Chip
          label={props.label}
          onClick={handleClickOpen}
          sx={{ backgroundColor: props.color, color: 'white', fontFamily: 'Montserrat', zIndex: 10, }}
        />
        <Dialog
          open={open}
          onClose={handleClose}
          aria-labelledby="specifications-dialog-title"
          maxWidth="sm"
          fullWidth
        >
          <DialogTitle id="specifications-dialog-title" sx={{ bgcolor: props.color, color: 'white' }}>
            <Typography variant="h6">{props.label}</Typography>
          </DialogTitle>
          <DialogContent dividers>
            <Grid container spacing={3}>
              {[
                { label: "Service Tag / SN", value: component.specifications.serialNo, key: "serialNo" },
                { label: "Make / Model", value: component.specifications.makeModel, key: "makeModel" },
                ...(component.type === 1 ? [
                  { label: "RAM (GB)", value: component.specifications.ram, key: "ram" },
                  { label: "HDD (GB)", value: component.specifications.hdd, key: "hdd" },
                  { label: "Processor", value: component.specifications.processor, key: "processor" },
                ] : []),
                ...(component.type === 3 ? [
                  { label: "No. of ports", value: component.specifications.ports, key: "ports" },
                ] : []),
                ...(component.type === 4 ? [
                  { label: "No. of drives", value: component.specifications.drives, key: "drives" },
                  { label: "No. of slots", value: component.specifications.slots, key: "slots" },
                ] : []),
                { label: "Vendor", value: `${component.specifications?.vendorID.vendorID} (${component.specifications?.vendorID.vendorName})`, key: "vendor" },
              ].map((item) => (
                <React.Fragment key={item.key}>
                  <Grid item xs={5}>
                    <Typography variant="subtitle2" color="text.secondary">{item.label}</Typography>
                  </Grid>
                  <Grid item xs={7}>
                    {isEditing ? (
                      <TextField
                        fullWidth
                        variant="outlined"
                        size="small"
                        value={item.value}
                        onChange={(e) => handleInputChange(item.key, e.target.value)}
                      />
                    ) : (
                      <Typography variant="body2">{item.value}</Typography>
                    )}
                  </Grid>
                </React.Fragment>
              ))}
            </Grid>
          </DialogContent>
          <DialogActions sx={{ justifyContent: 'flex-end', p: 2 }}>
            {isAdmin && !isEditing && (
              <Button variant="outlined" color="primary" onClick={handleEdit}>Edit</Button>
            )}
            {isAdmin && isEditing && isChanged && (
              <Button variant="contained" color="primary" onClick={handleSave}>Save</Button>
            )}
            <Button variant="outlined" color="secondary" onClick={handleClose}>Close</Button>
          </DialogActions>
        </Dialog>
      </>
      : <></>
  )
}

export default Badge
