import { Button, Chip, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Grid, MenuItem, Select, TextField, Typography } from '@mui/material'
import axios from 'axios';
import React, { useState, useEffect } from 'react'
import { FaArrowRight } from 'react-icons/fa';


const Badge = (props) => {
  const [open, setOpen] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editedComponent, setEditedComponent] = useState({});
  const [isChanged, setIsChanged] = useState(false);
  const [showChanges, setShowChanges] = useState(false);
  const [vendors, setVendors] = useState([]);



  useEffect(() => {
    setIsAdmin(localStorage.getItem('isAdmin') === 'true');
    fetchVendors();
  }, []);

  const fetchVendors = async () => {
    try {
      const response = await axios.get('/general/vendors');
      setVendors(response.data);
    } catch (error) {
      console.error('Error fetching vendors:', error);
    }
  };

  const handleClickOpen = () => {
    setOpen(true);
    setEditedComponent(props.componentDetails);
  };

  const handleClose = () => {
    setOpen(false);
    setIsEditing(false);
    setIsChanged(false);
  };

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    setShowChanges(true);
  };

  const confirmSave = async () => {
    try {
      setIsEditing(false);
      setIsChanged(false);
      setShowChanges(false);
  
      console.log("Old details: ", props.componentDetails);
      console.log("Sending new component details: ", editedComponent);
  
      const response = await axios.put(`/general/edit-component/${editedComponent._id}`, {
        specifications: {
          ...editedComponent.specifications,
          vendorID: {
            vendorID: parseInt(editedComponent.specifications.vendorID.vendorID),
            vendorName: editedComponent.specifications.vendorID.vendorName
          }
        }
      });
  
      if (response.status === 200) {
        console.log('Component updated successfully');
        // You might want to update the local state or trigger a refresh of the parent component
        props.onComponentUpdate(response.data);
      } else {
        throw new Error('Failed to update component');
      }
  
      handleClose();
    } catch (error) {
      console.error('Error updating component:', error);
      // Handle error (e.g., show an error message to the user)
    }
  };


  const handleInputChange = (key, value) => {
    setEditedComponent(prev => {
      const updated = {
        ...prev,
        specifications: {
          ...prev.specifications,
        }
      };

      if (key === 'vendor') {
        const matches = value.match(/^(.+?)\((.+?)\)$/);
        if (matches) {
          updated.specifications.vendorID = {
            vendorID: Number(matches[1]),
            vendorName: matches[2]
          };
        }
      } else {
        const originalValue = props.componentDetails.specifications[key];
        let typedValue = value;

        if (typeof originalValue === 'number' && !isNaN(Number(value))) {
          typedValue = Number(value);
        } else if (typeof originalValue === 'boolean') {
          typedValue = value === 'true' || value === true;
        }

        updated.specifications[key] = typedValue;
      }

      const hasChanged = isObjectChanged(updated, props.componentDetails);
      setIsChanged(hasChanged);

      return updated;
    });
  };

  // Helper function to deep compare objects
  const isObjectChanged = (obj1, obj2) => {
    const keys1 = Object.keys(obj1);
    const keys2 = Object.keys(obj2);

    if (keys1.length !== keys2.length) {
      return true;
    }

    for (const key of keys1) {
      const val1 = obj1[key];
      const val2 = obj2[key];
      const areObjects = isObject(val1) && isObject(val2);

      if (
        (areObjects && isObjectChanged(val1, val2)) ||
        (!areObjects && val1 !== val2)
      ) {
        return true;
      }
    }

    return false;
  };

  const isObject = (object) => {
    return object != null && typeof object === 'object';
  };

  const renderComparisonField = (label, oldValue, newValue, key) => (

    <React.Fragment key={key}>
      <Grid item xs={4}>
        <Typography variant="subtitle2" color="text.secondary">{label}</Typography>
      </Grid>
      <Grid item xs={3}>
        <Typography variant="body2">{oldValue}</Typography>
      </Grid>
      <Grid item xs={1}>
        <FaArrowRight />
      </Grid>
      <Grid item xs={4}>
        <Typography variant="body2" color="primary" textAlign={'center'}>{newValue}</Typography>
      </Grid>
    </React.Fragment>

  );

  const renderField = (item) => {
    if (isEditing) {
      if (item.key === 'vendor') {
        const currentVendorValue = `${item.value.vendorID}(${item.value.vendorName})`;
        return (
          <Select
            fullWidth
            value={currentVendorValue}
            onChange={(e) => handleInputChange(item.key, e.target.value)}
          >
            {vendors.map((vendor) => {
              const vendorValue = `${vendor.vendorID}(${vendor.vendorName})`;
              return (
                <MenuItem key={vendor._id} value={vendorValue}>
                  {vendorValue}
                </MenuItem>
              );
            })}
          </Select>
        );
      } else if (item.key === 'ram' || item.key === 'hdd') {
        return (
          <TextField
            fullWidth
            variant="outlined"
            size="small"
            type="number"
            value={item.value}
            onChange={(e) => handleInputChange(item.key, e.target.value)}
          />
        );
      } else {
        return (
          <TextField
            fullWidth
            variant="outlined"
            size="small"
            value={item.value}
            onChange={(e) => handleInputChange(item.key, e.target.value)}
          />
        );
      }
    } else {
      if (item.key === 'vendor') {
        return <Typography variant="body2">{`${item.value.vendorID}(${item.value.vendorName})`}</Typography>;
      }
      return <Typography variant="body2">{item.value}</Typography>;
    }
  };



  let component = isEditing ? editedComponent : props.componentDetails;


  return (
    props.label ?
      <>
        <Chip
          label={props.label}
          onClick={handleClickOpen}
          sx={{ backgroundColor: props.color, color: 'white', fontFamily: 'Montserrat', zIndex: 10, }}
        />
        <Dialog
          open={open}
          onClose={handleClose}
          aria-labelledby="specifications-dialog-title"
          maxWidth="sm"
          fullWidth
        >
          <DialogTitle id="specifications-dialog-title" sx={{ bgcolor: props.color, color: 'white' }}>
            <Typography variant="h6">{props.label}</Typography>
          </DialogTitle>
          <DialogContent dividers>
            {showChanges ? (
              <Grid container spacing={3}>
                {[
                  { label: "Service Tag / SN", oldValue: props.componentDetails.specifications.serialNo, newValue: editedComponent.specifications.serialNo, key: "serialNo" },
                  { label: "Make / Model", oldValue: props.componentDetails.specifications.makeModel, newValue: editedComponent.specifications.makeModel, key: "makeModel" },
                  ...(props.componentDetails.type === 1 ? [
                    { label: "RAM (GB)", oldValue: props.componentDetails.specifications.ram, newValue: editedComponent.specifications.ram, key: "ram" },
                    { label: "HDD (GB)", oldValue: props.componentDetails.specifications.hdd, newValue: editedComponent.specifications.hdd, key: "hdd" },
                    { label: "Processor", oldValue: props.componentDetails.specifications.processor, newValue: editedComponent.specifications.processor, key: "processor" },
                  ] : []),
                  ...(props.componentDetails.type === 3 ? [
                    { label: "No. of ports", oldValue: props.componentDetails.specifications.ports, newValue: editedComponent.specifications.ports, key: "ports" },
                  ] : []),
                  ...(props.componentDetails.type === 4 ? [
                    { label: "No. of drives", oldValue: props.componentDetails.specifications.drives, newValue: editedComponent.specifications.drives, key: "drives" },
                    { label: "No. of slots", oldValue: props.componentDetails.specifications.slots, newValue: editedComponent.specifications.slots, key: "slots" },
                  ] : []),
                  {
                    label: "Vendor",
                    oldValue: `${props.componentDetails.specifications?.vendorID.vendorID} (${props.componentDetails.specifications?.vendorID.vendorName})`,
                    newValue: `${editedComponent.specifications?.vendorID.vendorID} (${editedComponent.specifications?.vendorID.vendorName})`,
                    key: "vendor"
                  },
                ].map((item) => {
                  return (item.oldValue !== item.newValue && renderComparisonField(item.label, item.oldValue, item.newValue, item.key));

                }
                )}
              </Grid>
            ) : (

              <Grid container spacing={3}>
                {[
                  { label: "Service Tag / SN", value: component.specifications.serialNo, key: "serialNo" },
                  { label: "Make / Model", value: component.specifications.makeModel, key: "makeModel" },
                  ...(component.type === 1 ? [
                    { label: "RAM (GB)", value: component.specifications.ram, key: "ram" },
                    { label: "HDD (GB)", value: component.specifications.hdd, key: "hdd" },
                    { label: "Processor", value: component.specifications.processor, key: "processor" },
                  ] : []),
                  ...(component.type === 3 ? [
                    { label: "No. of ports", value: component.specifications.ports, key: "ports" },
                  ] : []),
                  ...(component.type === 4 ? [
                    { label: "No. of drives", value: component.specifications.drives, key: "drives" },
                    { label: "No. of slots", value: component.specifications.slots, key: "slots" },
                  ] : []),
                  { label: "Vendor", value: component.specifications?.vendorID, key: "vendor" },
                ].map((item) => (
                  <React.Fragment key={item.key}>
                    <Grid item xs={5}>
                      <Typography variant="subtitle2" color="text.secondary">{item.label}</Typography>
                    </Grid>
                    <Grid item xs={7}>
                      {renderField(item)}
                    </Grid>
                  </React.Fragment>
                ))}
              </Grid>
            )}
          </DialogContent>
          <DialogActions sx={{ justifyContent: 'flex-end', p: 2 }}>
            {isAdmin && !isEditing && !showChanges && (
              <Button variant="outlined" color="primary" onClick={handleEdit}>Edit</Button>
            )}
            {isAdmin && isEditing && isChanged && !showChanges && (
              <Button variant="contained" color="primary" onClick={handleSave}>Save</Button>
            )}
            {showChanges && (
              <>
                <Button variant="outlined" color="secondary" onClick={() => setShowChanges(false)}>Back</Button>
                <Button variant="contained" color="primary" onClick={confirmSave}>Confirm</Button>
              </>
            )}
            {!showChanges && (
              <Button variant="outlined" color="secondary" onClick={handleClose}>Close</Button>
            )}
          </DialogActions>
        </Dialog>
      </>
      : <></>
  )
}

export default Badge
