import React, { useState } from 'react';
import { NavLink, Outlet } from 'react-router-dom';

const Admin = () => {
  const [activeTab, setActiveTab] = useState('');

  const tabs = [
    { path: '', label: 'G&G DATA CENTER, WOB' },
    { path: 'vrc', label: 'VRC, WOB' },
    { path: 'dr', label: 'DR CENTER, WOB' },
    { path: 'spic', label: 'SPIC, WOB' },
  ];

  return (
    <div className="w-full flex flex-col">
      <nav className="fixed w-full z-10">
        <ul className="grid grid-cols-4 shadow-md">
          {tabs.map(({ path, label }) => (
            <li key={path}>
              <NavLink
                to={path}
                className={
                  activeTab === path
                    ? 'flex items-center justify-center h-14 bg-red-800 text-white transition-colors duration-300'
                    : 'flex items-center justify-center h-14 bg-white text-gray-700 hover:bg-gray-100 transition-colors duration-300'
                }
                onClick={() => setActiveTab(path)}
              >
                {label}
              </NavLink>
            </li>
          ))}
        </ul>
      </nav>
      
      <main className="pt-20">
        <Outlet />
      </main>
    </div>
  );
};

export default Admin;
