import React from 'react';
import './Table.css';
import Badge from './Badge';

const colorMapping = {
    DELL: "rgba(255, 0, 0, 0.6)",
    KVM: "rgba(0, 0, 255, 0.6)",
    CISCO: "rgba(0, 128, 0, 0.6)",
    WOBSP01: "rgba(255, 165, 0, 0.6)",
    WOBLF01: "rgba(255, 255, 0, 0.6)",
    PATCH: "rgba(128, 0, 128, 0.6)",
    LIU: "rgba(255, 192, 203, 0.6)",
    STORAGE: "rgba(165, 42, 42, 0.6)",
    NODE: "rgba(0, 255, 255, 0.6)",
    IBM: "rgba(128, 128, 128, 0.6)",
    NEOSERIES: "rgba(255, 0, 255, 0.6)"
};

const getItemType = (item) => {
    const key = item.split(' ')[0];
    return colorMapping[key] || 'rgba(200, 200, 200, 0.6)'; // Default gray color
};

const Table = ({ rack, name }) => {
    const groupedRack = rack.reduce((acc, item, index) => {
        if (item.trim() === '') {
            acc.push({ item: '', index });
        } else if (acc.length === 0 || item !== acc[acc.length - 1].item) {
            acc.push({ item, index, isGroupStart: true });
        } else {
            acc[acc.length - 1].count = (acc[acc.length - 1].count || 1) + 1;
            acc[acc.length - 1].isGroupEnd = index === rack.length - 1 || item !== rack[index + 1];
        }
        return acc;
    }, []);

    return (
        <table className="w-full table-fixed text-center text-white bg-teal-600">
            <thead className="text-3xl">
                <tr>
                    <th className="py-2">{name} Rack</th>
                </tr>
            </thead>
            <tbody>
                {groupedRack.map(({ item, index, isGroupStart, isGroupEnd, count = 1 }, groupIndex) => {
                    if (item === '') return null; // Skip empty items
                    const color = getItemType(item);
                    return (
                        <tr key={groupIndex} className={`border-t-2 border-b-2 border-white`}>
                            <td className="h-12 " style={{ height: `${count * 3}rem` }}>
                                <div className="absolute inset-0 flex items-center">
                                    <span className="pl-2 absolute left-0">
                                        {isGroupStart ? `${index + 1}.` : ''}
                                    </span>
                                    <div className="w-full flex justify-center items-center">
                                        <span className="w-10/12">
                                            <Badge label={item} color={color} />
                                        </span>
                                    </div>
                                </div>
                            </td>
                        </tr>
                    );
                })}
            </tbody>
        </table>
    );
};

export default Table;
