import React from 'react';
import './Table.css';
import Badge from './Badge';

const Table = ({ rack, name }) => {
  // Calculate group information based on components
  const groupedRack = rack.reduce((acc, item, index) => {
    if (acc.length === 0 || item.name !== acc[acc.length - 1].name) {
      acc.push({ ...item, startIndex: item.startSlot, endIndex: item.startSlot + item.size - 1, count: 1 });
    } else {
      acc[acc.length - 1].endIndex = item.startSlot + item.size - 1;
      acc[acc.length - 1].count += 1;
    }
    return acc;
  }, []);
  
  // ... rest of the code for creating componentSlots object
  

  // Create object with component names as keys and slot ranges as values
  const componentSlots = groupedRack.reduce((acc, { name, startIndex, endIndex }) => {
    acc[name] = `${startIndex + 1}-${endIndex + 1}`;
    return acc;
  }, {});

  // Log groupedRack and componentSlots for debugging (optional)
  console.log("Grouped", groupedRack);
  console.log("Component Slots", componentSlots);

  return (
    <table className="w-full table-fixed text-center text-white bg-teal-600 -z-0">
      <thead className="text-3xl">
        <tr>
          <th className="py-2">{name} Rack</th>
        </tr>
      </thead>
      <tbody>
        {groupedRack.map(({ name, startIndex, endIndex, count, color }, groupIndex) => {
          const indexDisplay = count > 1 ? `${startIndex + 1}-${endIndex + 1}` : `${startIndex + 1}`;

          return (
            <tr key={groupIndex} className="">
              <td className=" relative" style={{ height: `${count * 3}rem` }}>
                <div className="absolute inset-0 flex items-center">
                  <span className="pl-2 absolute left-0">
                    {indexDisplay}.
                  </span>
                  <div className="w-full flex justify-center items-center">
                    <span className="w-10/12">
                      {name ? (
                        <Badge label={name} color={color} />
                      ) : (
                        <div className="h-full w-full bg-transparent" />
                      )}
                    </span>
                  </div>
                </div>
              </td>
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};

export default Table;
