import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  Box, Container, Typography, Grid, Paper, Select, MenuItem, FormControl, InputLabel,
  Card, CardContent, Chip, Accordion, AccordionSummary, AccordionDetails, LinearProgress
} from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import StorageIcon from '@mui/icons-material/Storage';
import MemoryIcon from '@mui/icons-material/Memory';
import RouterIcon from '@mui/icons-material/Router';
import BackupIcon from '@mui/icons-material/Backup';
import DevicesOtherIcon from '@mui/icons-material/DevicesOther';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

const componentIcons = {
  1: <StorageIcon />,
  2: <MemoryIcon />,
  3: <RouterIcon />,
  4: <BackupIcon />,
  5: <DevicesOtherIcon />,
};

const Report = () => {
  const [components, setComponents] = useState({});
  const [filteredComponents, setFilteredComponents] = useState({});
  const [dataCenters, setDataCenters] = useState([]);
  const [vendors, setVendors] = useState([]);
  const [filters, setFilters] = useState({
    type: '',
    vendor: '',
    dataCenter: '',
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    applyFilters();
  }, [components, filters]);

  const fetchData = async () => {
    setLoading(true);
    try {
      const [componentsRes, dataCentersRes, vendorsRes] = await Promise.all([
        axios.get('/general/components'),
        axios.get('/general/data-centers'),
        axios.get('/general/vendors')
      ]);
      setComponents(componentsRes.data);
      setDataCenters(dataCentersRes.data);
      setVendors(vendorsRes.data);
      
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const applyFilters = () => {
    
    const filtered = Object.entries(components).reduce((acc, [rackName, rackComponents]) => {
      // Ensure rackComponents is an array
      const componentsArray = Array.isArray(rackComponents) ? rackComponents : [];
      
      const filteredRackComponents = componentsArray.filter(component => {
        return (
          (!filters.type || component.type === filters.type) &&
          (!filters.vendor || component.vendor === filters.vendor) &&
          (!filters.dataCenter || component.dataCenter === filters.dataCenter)
        );
      });
  
      if (filteredRackComponents.length > 0) {
        acc[rackName] = filteredRackComponents;
      }
  
      return acc;
    }, {});
  
    setFilteredComponents(filtered);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const componentTypes = {
    1: 'Server',
    2: 'Storage',
    3: 'Switch',
    4: 'Tape Drive',
    5: 'Other'
  };

  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="lg">
        <Box my={4}>
          <Typography variant="h3" component="h1" gutterBottom>
            Component Dashboard
          </Typography>
          
          <Grid container spacing={2} mb={4}>
            <Grid item xs={12} sm={4}>
              <FormControl fullWidth>
                <InputLabel>Type</InputLabel>
                <Select
                  name="type"
                  value={filters.type}
                  onChange={handleFilterChange}
                  label="Type"
                >
                  <MenuItem value="">All Types</MenuItem>
                  {Object.entries(componentTypes).map(([key, value]) => (
                    <MenuItem key={key} value={key}>
                      {componentIcons[key]} {value}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={4}>
              <FormControl fullWidth>
                <InputLabel>Vendor</InputLabel>
                <Select
                  name="vendor"
                  value={filters.vendor}
                  onChange={handleFilterChange}
                  label="Vendor"
                >
                  <MenuItem value="">All Vendors</MenuItem>
                  {vendors.map(vendor => (
                    <MenuItem key={vendor._id} value={vendor._id}>{vendor.name}</MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={4}>
              <FormControl fullWidth>
                <InputLabel>Data Center</InputLabel>
                <Select
                  name="dataCenter"
                  value={filters.dataCenter}
                  onChange={handleFilterChange}
                  label="Data Center"
                >
                  <MenuItem value="">All Data Centers</MenuItem>
                  {dataCenters.map(dc => (
                    <MenuItem key={dc._id} value={dc._id}>{dc.name}</MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
          </Grid>

          {loading ? (
            <LinearProgress />
          ) : (
            Object.entries(filteredComponents).map(([rackName, rackComponents]) => (
              <Accordion key={rackName} defaultExpanded>
                <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                  <Typography variant="h6">{rackName}</Typography>
                </AccordionSummary>
                <AccordionDetails>
                  <Grid container spacing={2}>
                    {rackComponents.sort((a, b) => a.startSlot - b.startSlot).map(component => (
                      <Grid item xs={12} sm={6} md={4} key={component._id}>
                        <Card elevation={3}>
                          <CardContent>
                            <Typography variant="h6" component="h2" gutterBottom>
                              {component.name}
                            </Typography>
                            <Box display="flex" alignItems="center" mb={1}>
                              {componentIcons[component.type]}
                              <Chip 
                                label={componentTypes[component.type] || component.type} 
                                color="primary" 
                                size="small" 
                                sx={{ ml: 1 }}
                              />
                            </Box>
                            <Typography variant="body2" color="text.secondary">
                              Slot: {component.startSlot}
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                              Slots Occupied: {component.size}
                            </Typography>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                </AccordionDetails>
              </Accordion>
            ))
          )}
        </Box>
      </Container>
    </ThemeProvider>
  );
};

export default Report;
