import React, { useState } from 'react';
import "./Table.css"; // Import your CSS file for styling

const Table = (props) => {
  const [serialNumber, setSerialNumber] = useState(1); // Starting serial number

  const handleRowRender = (item) => {
    return (
      <>
        <tr className="">
          <td className="h-10">{serialNumber}. {item}</td>
        </tr>
      </>
    );
  };

  return (
    <table className="w-full table-fixed text-center">
      <thead>
        <tr>
          <th>{props.name} Rack</th>
        </tr>
      </thead>
      <tbody>
        {props.rack?.map((item) => {
          setSerialNumber(serialNumber + 1); // Update serial number for each row
          return handleRowRender(item);
        })}
      </tbody>
    </table>
  );
};

export default Table;
