import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ChevronDownIcon, ChevronUpIcon } from '@heroicons/react/solid';

const componentTypes = {
  1: 'Server',
  2: 'Storage',
  3: 'Switch',
  4: 'Tape Drive',
  5: 'Other'
};

const componentIcons = {
  1: 'ðŸ–¥ï¸',
  2: 'ðŸ’¾',
  3: 'ðŸ”Œ',
  4: 'ðŸ“¼',
  5: 'ðŸ”§'
};

const Report = () => {
  const [components, setComponents] = useState([]);
  const [filteredComponents, setFilteredComponents] = useState({});
  const [dataCenters, setDataCenters] = useState([]);
  const [vendors, setVendors] = useState([]);
  const [filters, setFilters] = useState({
    type: '',
    vendor: '',
    dataCenter: '',
    search: '',
  });
  const [loading, setLoading] = useState(true);
  const [expandedRacks, setExpandedRacks] = useState({});

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    if (components.length > 0) {
      applyFilters();
    }
  }, [components, filters]);

  const fetchData = async () => {
    setLoading(true);
    try {
      const [componentsRes, dataCentersRes, vendorsRes] = await Promise.all([
        axios.get('/general/components'),
        axios.get('/general/data-centers'),
        axios.get('/general/vendors')
      ]);
      setComponents(componentsRes.data);
      setDataCenters(dataCentersRes.data);
      setVendors(vendorsRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const applyFilters = () => {
    const grouped = components.reduce((acc, component) => {
      const rackName = component.rackId.name;
      if (!acc[rackName]) {
        acc[rackName] = [];
      }
      acc[rackName].push(component);
      return acc;
    }, {});

    const filtered = Object.entries(grouped).reduce((acc, [rackName, rackComponents]) => {
      const filteredRackComponents = rackComponents.filter(component => {
        return (
          (!filters.type || component.type.toString() === filters.type) &&
          (!filters.vendor || component.specifications.vendorID._id === filters.vendor) &&
          (!filters.dataCenter || component.dataCenterId === filters.dataCenter) &&
          (!filters.search || component.name.toLowerCase().includes(filters.search.toLowerCase()))
        );
      });

      if (filteredRackComponents.length > 0) {
        acc[rackName] = filteredRackComponents;
      }

      return acc;
    }, {});

    setFilteredComponents(filtered);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const handleSearch = () => {
    applyFilters();
  };

  const toggleRack = (rackName) => {
    setExpandedRacks(prev => ({...prev, [rackName]: !prev[rackName]}));
  };

  const handleDelete = (componentId) => {
    // Implement your delete logic here
    console.log(`Delete component with id: ${componentId}`);
  };

  return (
    <div className="bg-gray-100 p-6 rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-6 text-gray-800 border-b pb-2">Component Dashboard</h2>
      
      {/* Filters */}
      <div className="mb-6 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
        <select 
          name="type"
          value={filters.type}
          onChange={handleFilterChange}
          className="w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-red-500 focus:border-red-500"
        >
          <option value="">All Types</option>
          {Object.entries(componentTypes).map(([key, value]) => (
            <option key={key} value={key}>{value}</option>
          ))}
        </select>
        <select 
          name="vendor"
          value={filters.vendor}
          onChange={handleFilterChange}
          className="w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-red-500 focus:border-red-500"
        >
          <option value="">All Vendors</option>
          {vendors.map(vendor => (
            <option key={vendor._id} value={vendor._id}>{vendor.vendorName}</option>
          ))}
        </select>
        <select 
          name="dataCenter"
          value={filters.dataCenter}
          onChange={handleFilterChange}
          className="w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-red-500 focus:border-red-500"
        >
          <option value="">All Data Centers</option>
          {dataCenters.map(dc => (
            <option key={dc._id} value={dc._id}>{dc.name}</option>
          ))}
        </select>
        <input
          type="text"
          name="search"
          value={filters.search}
          onChange={handleFilterChange}
          placeholder="Search Components"
          className="w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-red-500 focus:border-red-500"
        />
      </div>
      <button 
        onClick={handleSearch}
        className="w-full bg-red-800 text-white p-2 rounded-md hover:bg-red-700 transition-colors duration-150 ease-in-out mb-6"
      >
        Search
      </button>

      {loading ? (
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-red-800 mx-auto"></div>
        </div>
      ) : Object.keys(filteredComponents).length === 0 ? (
        <p className="text-center text-gray-600 mt-4">No components found matching the current filters.</p>
      ) : (
        Object.entries(filteredComponents).map(([rackName, rackComponents]) => (
          <div key={rackName} className="mb-4 bg-white rounded-lg shadow-md overflow-hidden">
            <button
              onClick={() => toggleRack(rackName)}
              className="w-full text-left text-lg font-semibold bg-red-800 text-white p-4 flex justify-between items-center hover:bg-red-700 transition-colors duration-150 ease-in-out"
            >
              {rackName}
              {expandedRacks[rackName] ? (
                <ChevronUpIcon className="h-5 w-5" />
              ) : (
                <ChevronDownIcon className="h-5 w-5" />
              )}
            </button>
            {expandedRacks[rackName] && (
              <ul className="divide-y divide-gray-200">
                {[...rackComponents].sort((a, b) => a.startSlot - b.startSlot).map(component => (
                  <li key={component._id} className="p-4 hover:bg-gray-50 transition-colors duration-150 ease-in-out">
                    <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center space-y-2 sm:space-y-0">
                      <div className="flex-1">
                        <span className="font-medium text-gray-800">{component.name}</span>
                        <div className="text-sm text-gray-600 mt-1">
                          <span className="inline-block bg-blue-100 text-blue-800 rounded-full px-2 py-1 text-xs font-semibold mr-2">
                            {componentIcons[component.type]} {componentTypes[component.type] || component.type}
                          </span>
                          <span className="mr-2">Slot: {component.startSlot}</span>
                          <span>Slots Occupied: {component.size}</span>
                        </div>
                      </div>
                      <div className="flex">
                        <button
                          onClick={() => handleDelete(component._id)}
                          className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-md text-sm font-medium transition-colors duration-150 ease-in-out focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-opacity-50"
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default Report;
