import React from 'react';
import './Table.css';
import Badge from './Badge';

const colorMapping = {
    DELL: "rgba(255, 0, 0, 0.6)",       
    KVM: "rgba(0, 0, 255, 0.6)",        
    CISCO: "rgba(0, 128, 0, 0.6)",      
    WOBSP01: "rgba(255, 165, 0, 0.6)",  
    WOBLF01: "rgba(255, 255, 0, 0.6)",  
    PATCH: "rgba(128, 0, 128, 0.6)",    
    LIU: "rgba(255, 192, 203, 0.6)",   
    STORAGE: "rgba(165, 42, 42, 0.6)",  
    NODE: "rgba(0, 255, 255, 0.6)",    
    IBM: "rgba(128, 128, 128, 0.6)",   
    NEOSERIES: "rgba(255, 0, 255, 0.6)" 
};

const getItemType = (item) => {
    const key = item.split(' ')[0];
    return colorMapping[key] || 'defaultColor'; // Fallback color
};

const Table = (props) => {
    const groupedRack = props.rack.reduce((acc, item, index) => {
        if (item.trim() === '') {
            acc.push({ item: '', index });
        } else if (acc.length === 0 || item !== acc[acc.length - 1].item) {
            acc.push({ item, index, isGroupStart: true });
        } else {
            acc.push({ item, index, isGroupEnd: index === props.rack.length - 1 || item !== props.rack[index + 1] });
        }
        return acc;
    }, []);

    return (
        <table className='w-full table-fixed text-center text-white bg-teal-600 -z-0'>
            <thead className='text-3xl'>
                <tr>
                    <th className='py-2'>{props.name} Rack</th>
                </tr>
            </thead>
            <tbody>
                {groupedRack.map(({ item, index, isGroupStart, isGroupEnd }, groupIndex) => {
                    if (item === '') return null; // Skip empty items
                    const color = getItemType(item);
                    return (
                        <tr key={groupIndex} className={`${isGroupStart ? 'border-t-2 border-white' : ''} ${isGroupEnd ? 'border-b-2 border-white' : ''}`}>
                            <td className='h-12'>
                                <div className='flex justify-start'>
                                    <span className='pl-2 flex'>
                                        {isGroupStart ? `${index + 1}.` : ''}
                                    </span>
                                    <div className='w-full flex justify-center'>
                                        <span className='w-10/12 flex justify-center'>
                                            <Badge label={item} color={color} />
                                        </span>
                                    </div>
                                </div>
                            </td>
                        </tr>
                    );
                })}
            </tbody>
        </table>
    );
};

export default Table;
