import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  ExpandMore as ExpandMoreIcon, 
  Storage as StorageIcon, 
  Memory as MemoryIcon, 
  Router as RouterIcon, 
  Backup as BackupIcon, 
  DevicesOther as DevicesOtherIcon, 
  Search as SearchIcon 
} from '@mui/icons-material';
import { 
  Accordion, 
  AccordionSummary, 
  AccordionDetails, 
  Card, 
  CardContent, 
  Typography, 
  Chip 
} from '@mui/material';

import SpecificationsDialog from './reusable/SpecificationsDialog'; // Assuming you have this component

const componentIcons = {
  1: <StorageIcon />,
  2: <MemoryIcon />,
  3: <RouterIcon />,
  4: <BackupIcon />,
  5: <DevicesOtherIcon />,
};

const componentTypes = {
  1: 'Server',
  2: 'Storage',
  3: 'Switch',
  4: 'Tape Drive',
  5: 'Other'
};

const componentColors = {
  1: '#4caf50', // green
  2: '#2196f3', // blue
  3: '#ff9800', // orange
  4: '#9c27b0', // purple
  5: '#607d8b', // blue-grey
};

const Report = () => {
  const [refreshTrigger, setRefreshTrigger] = useState(0);
  const [components, setComponents] = useState([]);
  const [filteredComponents, setFilteredComponents] = useState({});
  const [dataCenters, setDataCenters] = useState([]);
  const [vendors, setVendors] = useState([]);
  const [filters, setFilters] = useState({
    type: '',
    vendor: '',
    dataCenter: '',
    search: '',
  });
  const [loading, setLoading] = useState(true);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [selectedComponent, setSelectedComponent] = useState(null);
  const [isAdmin] = useState(true); // Set this based on your authentication logic

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    if (components.length > 0) {
      applyFilters();
    }
  }, [components, filters]);

  
  

  const fetchData = async () => {
    setLoading(true);
    try {
      const [componentsRes, dataCentersRes, vendorsRes] = await Promise.all([
        axios.get('/general/components'),
        axios.get('/general/data-centers'),
        axios.get('/general/vendors')
      ]);
      setComponents(componentsRes.data);
      setDataCenters(dataCentersRes.data);
      setVendors(vendorsRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const applyFilters = () => {
    const groupedByRack = components.reduce((acc, component) => {
      const rackName = component.rackId.name;
      if (!acc[rackName]) {
        acc[rackName] = [];
      }
      acc[rackName].push(component);
      return acc;
    }, {});
  
    const filtered = Object.entries(groupedByRack).reduce((acc, [rackName, rackComponents]) => {
      const filteredRackComponents = rackComponents.filter(component => {
        return (
          (!filters.type || component.type.toString() === filters.type) &&
          (!filters.vendor || component.specifications.vendorID._id === filters.vendor) &&
          (!filters.dataCenter || component.dataCenterId === filters.dataCenter) &&
          (!filters.search || component.name.toLowerCase().includes(filters.search.toLowerCase()))
        );
      });
  
      if (filteredRackComponents.length > 0) {
        acc[rackName] = filteredRackComponents;
      }
  
      return acc;
    }, {});
  
    setFilteredComponents(filtered);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const handleSearch = () => {
    applyFilters();
  };

  const handleComponentClick = (component) => {
    setSelectedComponent(component);
    setDialogOpen(true);
  };

  const handleDialogClose = () => {
    setDialogOpen(false);
    setSelectedComponent(null);
  };

  const handleComponentSave = (updatedComponent) => {
    // Implementing the save logic here
    setRefreshTrigger(prev => prev + 1);
    console.log('Saving updated component:', updatedComponent);
    setDialogOpen(false);
  };

  return (
    <div className="bg-red-50 min-h-screen">
      <header className="bg-white text-red-800 p-4 shadow-md">
        <h1 className="text-2xl font-bold">Component Dashboard</h1>
      </header>
      <main className="container mx-auto mt-8 px-4">
      <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <h2 className="text-xl font-semibold mb-4">Filters</h2>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <select
              name="type"
              value={filters.type}
              onChange={handleFilterChange}
              className="w-full p-2 border border-gray-300 rounded"
            >
              <option value="">All Types</option>
              {Object.entries(componentTypes).map(([key, value]) => (
                <option key={key} value={key}>
                  {value}
                </option>
              ))}
            </select>
            <select
              name="vendor"
              value={filters.vendor}
              onChange={handleFilterChange}
              className="w-full p-2 border border-gray-300 rounded"
            >
              <option value="">All Vendors</option>
              {vendors.map(vendor => (
                <option key={vendor._id} value={vendor._id}>{vendor.vendorName}</option>
              ))}
            </select>
            <select
              name="dataCenter"
              value={filters.dataCenter}
              onChange={handleFilterChange}
              className="w-full p-2 border border-gray-300 rounded"
            >
              <option value="">All Data Centers</option>
              {dataCenters.map(dc => (
                <option key={dc._id} value={dc._id}>{dc.name}</option>
              ))}
            </select>
            <input
              type="text"
              name="search"
              placeholder="Search Components"
              value={filters.search}
              onChange={handleFilterChange}
              className="w-full p-2 border border-gray-300 rounded"
            />
          </div>
          <button
            onClick={handleSearch}
            className="mt-4 w-full bg-red-800 text-white py-2 px-4 rounded hover:bg-red-700 transition duration-300"
          >
            <SearchIcon className="w-5 h-5 inline-block mr-2" />
            Search
          </button>
        </div>



        {loading ? (
          <div className="text-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-red-800 mx-auto"></div>
          </div>
        ) : Object.keys(filteredComponents).length === 0 ? (
          <p className="text-center text-xl mt-8">
            No components found matching the current filters.
          </p>
        ) : (
          Object.entries(filteredComponents).map(([rackName, rackComponents]) => (
            <Accordion key={rackName} className="mb-4">
              <AccordionSummary
                expandIcon={<ExpandMoreIcon />}
                aria-controls={`panel-${rackName}-content`}
                id={`panel-${rackName}-header`}
                className="bg-red-800 text-white"
              >
                <Typography variant="h6">{rackName}</Typography>
              </AccordionSummary>
              <AccordionDetails>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {rackComponents.sort((a, b) => a.startSlot - b.startSlot).map(component => (
                    <Card 
                      key={component._id} 
                      className="cursor-pointer hover:shadow-lg transition-shadow duration-300"
                      onClick={() => handleComponentClick(component)}
                    >
                      <CardContent>
                        <Typography variant="h6" component="h4" gutterBottom>
                          {component.name}
                        </Typography>
                        <div className="flex items-center mb-2">
                          {componentIcons[component.type]}
                          <Chip 
                            label={componentTypes[component.type] || component.type}
                            size="small"
                            style={{ 
                              backgroundColor: componentColors[component.type], 
                              color: 'white',
                              marginLeft: '8px'
                            }}
                          />
                        </div>
                        <Typography variant="body2" color="text.secondary">
                          Slot: {component.startSlot}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          Slots Occupied: {component.size}
                        </Typography>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </AccordionDetails>
            </Accordion>
          ))
        )}
      </main>

      {selectedComponent && (
        <SpecificationsDialog
          open={dialogOpen}
          onClose={handleDialogClose}
          componentDetails={selectedComponent}
          color={componentColors[selectedComponent.type]}
          label={componentTypes[selectedComponent.type]}
          isAdmin={isAdmin}
          onSave={handleComponentSave}
          
        />
      )}
    </div>
  );
};

export default Report;
