import { Button, Chip, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Grid, TextField } from '@mui/material'
import React, { useState, useEffect } from 'react'

const Badge = (props) => {
    const [open, setOpen] = useState(false);
    const [isAdmin, setIsAdmin] = useState(false);
    const [isEditing, setIsEditing] = useState(false);
    const [editedComponent, setEditedComponent] = useState({});
    const [isChanged, setIsChanged] = useState(false);

    useEffect(() => {
        setIsAdmin(localStorage.getItem('isAdmin') === 'true');
    }, []);

    const handleClickOpen = () => {
        setOpen(true);
        setEditedComponent(props.componentDetails);
    };

    const handleClose = () => {
        setOpen(false);
        setIsEditing(false);
        setIsChanged(false);
    };

    const handleEdit = () => {
        setIsEditing(true);
    };

    const handleSave = () => {
        // Here you would typically save the changes to the backend
        console.log('Saving changes:', editedComponent);
        setIsEditing(false);
        setIsChanged(false);
        // Update the original component details
        props.onComponentUpdate(editedComponent);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setEditedComponent(prev => {
            const updated = {
                ...prev,
                specifications: {
                    ...prev.specifications,
                    [name]: value
                }
            };
    
            // Special handling for vendor
            if (name === 'vendor') {
                const [vendorID, vendorName] = value.split('(');
                updated.specifications.vendorID = {
                    vendorID: vendorID.trim(),
                    vendorName: vendorName.replace(')', '').trim()
                };
            }
    
            setIsChanged(JSON.stringify(updated) !== JSON.stringify(props.componentDetails));
            return updated;
        });
    };

    const renderField = (label, value, name) => {
        return isEditing ? (
            <TextField
                name={name}
                value={value}
                onChange={handleInputChange}
                fullWidth
                margin="dense"
            />
        ) : (
            <DialogContentText>{value}</DialogContentText>
        );
    };

    let component = isEditing ? editedComponent : props.componentDetails;

   
return (
    props.label ?
        <>
            <Chip
                label={props.label}
                onClick={handleClickOpen}
                sx={{ backgroundColor: props.color, color: 'white', fontFamily: 'Montserrat', zIndex: 10 }}
            />
            <Dialog
                open={open}
                onClose={handleClose}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">
                    <span>
                    Specifications :
                    </span>
                    <span className=' text-base ml-2'>
                    {props.label}
                    </span>
                </DialogTitle>
                <DialogContent>
                    <Grid container spacing={2}>
                        <Grid item xs={4} className=" flex  items-center">
                            <DialogContentText>Service Tag / SN</DialogContentText>
                        </Grid>
                        <Grid item xs={1} className=" flex  items-center">
                            <DialogContentText>:</DialogContentText>
                        </Grid>
                        <Grid item xs={7}>
                            {renderField("Serial Number", component.specifications.serialNo, "serialNo")}
                        </Grid>

                        <Grid item xs={4} className=" flex  items-center">
                            <DialogContentText>Make / Model</DialogContentText>
                        </Grid>
                        <Grid item xs={1} className=" flex  items-center">
                            <DialogContentText>:</DialogContentText>
                        </Grid>
                        <Grid item xs={7}>
                            {renderField("Make/Model", component.specifications.makeModel, "makeModel")}
                        </Grid>

                        {component.type === 1 && (
                            <>
                                <Grid item xs={4} className=" flex  items-center">
                                    <DialogContentText>RAM (GB)</DialogContentText>
                                </Grid>
                                <Grid item xs={1} className=" flex  items-center">
                                    <DialogContentText>:</DialogContentText>
                                </Grid>
                                <Grid item xs={7}>
                                    {renderField("RAM (GB)", component.specifications.ram, "ram")}
                                </Grid>

                                <Grid item xs={4} className=" flex  items-center">
                                    <DialogContentText>HDD (GB)</DialogContentText>
                                </Grid>
                                <Grid item xs={1} className=" flex  items-center">
                                    <DialogContentText>:</DialogContentText>
                                </Grid>
                                <Grid item xs={7}>
                                    {renderField("HDD (GB)", component.specifications.hdd, "hdd")}
                                </Grid>

                                <Grid item xs={4} className=" flex  items-center">
                                    <DialogContentText>Processor</DialogContentText>
                                </Grid>
                                <Grid item xs={1} className=" flex  items-center">
                                    <DialogContentText>:</DialogContentText>
                                </Grid>
                                <Grid item xs={7}>
                                    {renderField("Processor", component.specifications.processor, "processor")}
                                </Grid>
                            </>
                        )}

                        {component.type === 3 && (
                            <>
                                <Grid item xs={4} className=" flex  items-center">
                                    <DialogContentText>No. of ports</DialogContentText>
                                </Grid>
                                <Grid item xs={1} className=" flex  items-center">
                                    <DialogContentText>:</DialogContentText>
                                </Grid>
                                <Grid item xs={7}>
                                    {renderField("No. of ports", component.specifications.ports, "ports")}
                                </Grid>
                            </>
                        )}

                        {component.type === 4 && (
                            <>
                                <Grid item xs={4} className=" flex  items-center">
                                    <DialogContentText>No. of drives</DialogContentText>
                                </Grid>
                                <Grid item xs={1} className=" flex  items-center">
                                    <DialogContentText>:</DialogContentText>
                                </Grid>
                                <Grid item xs={7}>
                                    {renderField("No. of drives", component.specifications.drives, "drives")}
                                </Grid>

                                <Grid item xs={4} className=" flex  items-center">
                                    <DialogContentText>No. of slots</DialogContentText>
                                </Grid>
                                <Grid item xs={1} className=" flex  items-center">
                                    <DialogContentText>:</DialogContentText>
                                </Grid>
                                <Grid item xs={7}>
                                    {renderField("No. of slots", component.specifications.slots, "slots")}
                                </Grid>
                            </>
                        )}

                        <Grid item xs={4} className=" flex  items-center">
                            <DialogContentText>Vendor</DialogContentText>
                        </Grid>
                        <Grid item xs={1} className=" flex  items-center">
                            <DialogContentText>:</DialogContentText>
                        </Grid>
                        <Grid item xs={7}>
                            {renderField("Vendor", `${component.specifications?.vendorID.vendorID} (${component.specifications?.vendorID.vendorName})`, "vendor")}
                        </Grid>
                    </Grid>
                </DialogContent>
                <DialogActions>
                    {isAdmin && !isEditing && (
                        <Button onClick={handleEdit}>Edit</Button>
                    )}
                    {isAdmin && isEditing && isChanged && (
                        <Button onClick={handleSave}>Save</Button>
                    )}
                    <Button onClick={handleClose}>Close</Button>
                </DialogActions>
            </Dialog>
        </>
        : <></>
)
}

export default Badge
