import React from 'react';
import './Table.css';
import Badge from './Badge';

const Table = ({ rack }) => {
  const groupedRack = rack.reduce((acc, item, index) => {
    const { startSlot, size } = item; // Destructure startSlot and size
    const endIndex = startSlot + size - 1; // Calculate endIndex

    // Check if a gap exists between current and previous item
    const gap = acc.length > 0 && startSlot !== (acc[acc.length - 1].endIndex + 1);

    if (gap) {
      // Create an empty slot entry
      acc.push({ name: null, startIndex: acc[acc.length - 1].endIndex + 1, endIndex: startSlot - 1 });
    }

    acc.push({ ...item, startSlot, endIndex }); // Add current component

    return acc;
  }, []);

  return (
    <table className="w-full table-fixed text-center text-white bg-teal-600">
      <thead className="text-3xl">
        <tr>
          <th className="py-2">{rack[0]?.name} Rack</th> {/* Use only the name from the first rack item */}
        </tr>
      </thead>
      <tbody>
        {groupedRack.map(({ name, startIndex, endIndex, color }, groupIndex) => {
          const indexDisplay = endIndex >= startIndex ? `${startIndex + 1}-${endIndex + 1}` : `${startIndex + 1}`;

          return (
            <tr key={groupIndex} className="">
              <td className="h-12 relative" style={{ height: `${(endIndex - startIndex + 1) * 3}rem` }}>
                <div className="absolute inset-0 flex items-center">
                  {name ? (
                    <span className="pl-2 absolute left-0">
                      {indexDisplay}.
                    </span>
                  ) : null} {/* Only display index for components */}
                  <div className="w-full flex justify-center items-center">
                    <span className="w-10/12">
                      {name ? (
                        <Badge label={name} color={color} />
                      ) : (
                        <div className="h-full w-full bg-gray-300 rounded" /> 
                      )}
                    </span>
                  </div>
                </div>
              </td>
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};

export default Table;
