import React from 'react';
import './Table.css';
import Badge from './Badge';

export const TOTAL_SLOTS = 20;


const Table = ({ rack, name }) => {

  
  console.log(rack)
  const sortedRack = [...rack].sort((a, b) => a.startSlot - b.startSlot);
  // Function to combine rows based on slot ranges and add empty slots
  const combineRowsWithEmptySlots = (data) => {
    let currentSlot = 1;
    const combinedRows = [];
    
    for (const item of data) {
        // Add empty slots before the current item if there's a gap
        if (item.startSlot > currentSlot) {
            for (let i = currentSlot; i < item.startSlot; i++) {
                combinedRows.push({
                    name: '',
                    startSlot: i,
                    endSlot: i,
                    color: 'transparent', // You can choose a different color for empty slots
                    rows: 1,
                });
            }
        }
        console.log("Current Item",item.name)
        console.log("Current Slot",currentSlot)

      // Add the current item
      combinedRows.push({
        ...item,
          rows: item.size,
        endSlot: item.startSlot + item.size - 1
      });

      // Update the current slot to the next slot after the current item
      currentSlot = item.startSlot + item.size;
      }
      
      // Add empty slots at the end if needed
    if (currentSlot <= TOTAL_SLOTS) {
        for (let i = currentSlot; i <= TOTAL_SLOTS; i++) {
          combinedRows.push({
            name: '',
            startSlot: i,
            endSlot: i,
            color: 'transparent', // You can choose a different color for empty slots
            rows: 1,
          });
        }
      }
  

    return combinedRows;
  };

  // Combine rows using rack data and add empty slots
    const rowsWithRowCount = combineRowsWithEmptySlots(sortedRack);
    console.log("Rows with row count",rowsWithRowCount)

  return (
    <div className="bg-gradient-to-br from-blue-500 to-purple-600 p-6 shadow-2xl">
  <table className="w-full table-fixed text-center bg-white bg-opacity-10 backdrop-blur-lg">
    <thead className="text-white">
      <tr>
        <th className="py-6 px-6 text-3xl font-bold tracking-wider">{name} Rack</th>
      </tr>
    </thead>
    <tbody>
      {rowsWithRowCount.map(({ name, startSlot, endSlot, color, rows, _id }, groupIndex) => {
        const indexDisplay = startSlot === endSlot ? `${startSlot}` : `${startSlot}-${endSlot}`;
        const rowHeight = `${rows * 3}rem`;

        return (
          <tr 
            key={groupIndex} 
            className="border-b border-white border-opacity-20 hover:bg-white hover:bg-opacity-20 transition-all duration-300 ease-in-out"
          >
            <td
              className="relative"
              style={{ height: rowHeight }}
            >
              <div className="absolute inset-0 flex items-center">
                <span className="pl-4 absolute left-0 text-white text-opacity-70 font-medium text-lg">
                  {indexDisplay}
                </span>
                <div className="ml-16 w-full flex justify-center items-center">
                  <span className="w-10/12 transform hover:scale-105 transition-transform duration-200">
                    {name ? (
                      <Badge label={name} color={color} componentDetails={
                        _id ? rowsWithRowCount[groupIndex] : null
                      } />
                    ) : (
                      <div className="h-full w-full bg-white bg-opacity-5 backdrop-blur-sm" />
                    )}
                  </span>
                </div>
              </div>
            </td>
          </tr>
        );
      })}
    </tbody>
  </table>
</div>
  );
};

export default Table;
