import React, { useState } from 'react';
import { NavLink, Outlet } from 'react-router-dom';
import { motion } from 'framer-motion';

const Admin = () => {
  const [activeTab, setActiveTab] = useState(''); // Initial active tab is empty string

  const handleTabClick = (path) => {
    setActiveTab(path);
  };

  return (
    <>
      <div className='w-full flex-col'>
        <div className='grid grid-rows-1 grid-cols-4 transition-all fixed w-full z-10'>
          <NavLink
            to={''}
            className={({ isActive }) => (
              activeTab === '' // Check only against the activeTab state
                ? 'flex items-center justify-center h-12 bg-amber-300 border border-red-800 border-r-0 hover:bg-amber-400 duration-300'
                : 'flex items-center justify-center h-12 bg-slate-100 border border-red-800 border-r-0 hover:bg-slate-200 duration-300'
            )}
            onClick={() => handleTabClick('')}
          >
            G&G DATA CENTER , WOB
          </NavLink>
          <NavLink
            to={'vrc'}
            className={({ isActive }) => (
              activeTab === 'vrc' // Check only against the activeTab state
                ? 'flex items-center justify-center h-12 bg-amber-300 border border-red-800 border-r-0 hover:bg-amber-400 duration-300'
                : 'flex items-center justify-center h-12 bg-slate-100 border border-red-800 border-r-0 hover:bg-slate-200 duration-300'
            )}
            onClick={() => handleTabClick('vrc')}
          >
            VRC , WOB
          </NavLink>
          <NavLink
            to={'dr'}
            className={({ isActive }) => (
              activeTab === 'dr' // Check only against the activeTab state
                ? 'flex items-center justify-center h-12 bg-amber-300 border border-red-800 border-r-0 hover:bg-amber-400 duration-300'
                : 'flex items-center justify-center h-12 bg-slate-100 border border-red-800 border-r-0 hover:bg-slate-200 duration-300'
            )}
            onClick={() => handleTabClick('dr')}
          >
            DR CENTER , WOB
          </NavLink>
          <NavLink
            to={'spic'}
            className={({ isActive }) => (
              activeTab === 'spic' // Check only against the activeTab state
                ? 'flex items-center justify-center h-12 bg-amber-300 border border-red-800 border-r-0 hover:bg-amber-400 duration-300'
                : 'flex items-center justify-center h-12 bg-slate-100 border border-red-800 border-r-0 hover:bg-slate-200 duration-300'
            )}
            onClick={() => handleTabClick('spic')}
          >
            SPIC , WOB
          </NavLink>
        </div>
        
        <motion.div className='pt-20'
        
        animate={ { opacity: 1}}
        exit={{ opacity: 0 }}>
          <Outlet />
        </motion.div>
      </div>
    </>
  );
};

export default Admin;
