import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Report = () => {
  const [components, setComponents] = useState({});
  const [filteredComponents, setFilteredComponents] = useState({});
  const [dataCenters, setDataCenters] = useState([]);
  const [vendors, setVendors] = useState([]);
  const [filters, setFilters] = useState({
    type: '',
    vendor: '',
    dataCenter: '',
  });

  useEffect(() => {
    fetchComponents();
    fetchDataCenters();
    fetchVendors();
  }, []);

  useEffect(() => {
    applyFilters();
  }, [components, filters]);

  const fetchComponents = async () => {
    try {
      const response = await axios.get('/general/components');
      setComponents(response.data);
    } catch (error) {
      console.error('Error fetching components:', error);
    }
  };

  const fetchDataCenters = async () => {
    try {
      const response = await axios.get('/general/data-centers');
      setDataCenters(response.data);
    } catch (error) {
      console.error('Error fetching data centers:', error);
    }
  };

  const fetchVendors = async () => {
    try {
      const response = await axios.get('/general/vendors');
      setVendors(response.data);
    } catch (error) {
      console.error('Error fetching vendors:', error);
    }
  };

  const applyFilters = () => {
    const filtered = Object.entries(components).reduce((acc, [rackName, rackComponents]) => {
      const filteredRackComponents = rackComponents.filter(component => {
        return (
          (!filters.type || component.type === filters.type) &&
          (!filters.vendor || component.vendor === filters.vendor) &&
          (!filters.dataCenter || component.dataCenter === filters.dataCenter)
        );
      });

      if (filteredRackComponents.length > 0) {
        acc[rackName] = filteredRackComponents;
      }

      return acc;
    }, {});

    setFilteredComponents(filtered);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const componentTypes = {
    1: 'Server',
    2: 'Storage',
    3: 'Switch',
    4: 'Tape Drive',
    5: 'Other'
  };

  return (
    <div className="bg-gray-100 p-6 rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-6 text-gray-800 border-b pb-2">Component Dashboard</h2>

      <div className="mb-6 flex flex-wrap gap-4">
        <select
          name="type"
          value={filters.type}
          onChange={handleFilterChange}
          className="p-2 border rounded"
        >
          <option value="">All Types</option>
          {Object.entries(componentTypes).map(([key, value]) => (
            <option key={key} value={key}>{value}</option>
          ))}
        </select>

        <select
          name="vendor"
          value={filters.vendor}
          onChange={handleFilterChange}
          className="p-2 border rounded"
        >
          <option value="">All Vendors</option>
          {vendors.map(vendor => (
            <option key={vendor._id} value={vendor._id}>{vendor.name}</option>
          ))}
        </select>

        <select
          name="dataCenter"
          value={filters.dataCenter}
          onChange={handleFilterChange}
          className="p-2 border rounded"
        >
          <option value="">All Data Centers</option>
          {dataCenters.map(dc => (
            <option key={dc._id} value={dc._id}>{dc.name}</option>
          ))}
        </select>
      </div>

      {Object.entries(filteredComponents).map(([rackName, rackComponents]) => (
        <div key={rackName} className="mb-8 bg-white rounded-lg shadow-md overflow-hidden">
          <h3 className="text-lg font-semibold bg-red-800 text-white p-4">{rackName}</h3>
          <ul className="divide-y divide-gray-200">
            {[...rackComponents].sort((a, b) => a.startSlot - b.startSlot).map(component => (
              <li key={component._id} className="p-4 hover:bg-gray-50 transition-colors duration-150 ease-in-out">
                <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center space-y-2 sm:space-y-0">
                  <div className="flex-1">
                    <span className="font-medium text-gray-800">{component.name}</span>
                    <div className="text-sm text-gray-600 mt-1">
                      <span className="inline-block bg-blue-100 text-blue-800 rounded-full px-2 py-1 text-xs font-semibold mr-2">
                        {componentTypes[component.type] || component.type}
                      </span>
                      <span className="mr-2">Slot: {component.startSlot}</span>
                      <span>Slots Occupied: {component.size}</span>
                    </div>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default Report;
