import React, { useState, useEffect } from 'react'
import Table from '../reusable/Table'
import axios from "axios"

const GnG = () => {
    const [normalizedRacks, setNormalizedRacks] = useState({});

    // Updated color mapping based on 'type' field
    const colorMapping = {
        1: "rgba(255, 0, 0, 0.6)",    // type 1 is for servers
        2: "rgba(0, 0, 255, 0.6)",    // type 2 is for network devices
        3: "rgba(0, 128, 0, 0.6)",    // type 3 is for storage devices
        4: "rgba(255, 165, 0, 0.6)",  // type 4 is for others
        
    };

    const getItemType = (item) => {
        return colorMapping[item.type] || 'rgba(200, 200, 200, 0.6)'; // Default gray color
    };

    function normalizeArrayLengths(data, fillValue = "") {
   
        let maxLength = 0;

        for (const rack of Object.values(data)) {
          for (const component of rack) {
            maxLength = Math.max(maxLength, component.startSlot + component.size - 1);
          }
        }
      
        // Applying normalization 
        return Object.fromEntries(
          Object.entries(data).map(([key, value]) => [
            key,
            value.concat(Array(maxLength - value.length).fill(fillValue)),
          ])
        );
      }
      
    useEffect(() => {
        async function fetchData() {
            try {
                const response = await axios.get('/general/data-centers/1/components');
                const data = response.data;
                console.log("Raw data:", data);
                
                
                const processedData = Object.entries(data).reduce((acc, [rackName, components]) => {
                    acc[rackName] = components.map(component => ({
                        ...component,
                        color: getItemType(component)
                    }));
                    return acc;
                }, {});

                // const normalizedData = normalizeArrayLengths(processedData);
                setNormalizedRacks(processedData);
            } catch (error) {
                console.error("Error fetching components:", error);
                // Handle errors appropriately
            }
        }

        fetchData();
    }, []);

    if (Object.keys(normalizedRacks).length === 0) {
        return <div>Loading...</div>;
    }

    return (
        <div className='flex mx-2 border-r border-black'>
            {Object.entries(normalizedRacks).map(([rackName, components]) => (
                <Table key={rackName} rack={components} name={rackName} />
            ))}
        </div>
    )
}

export default GnG
