import React, { useState } from 'react';
import { ChevronDownIcon, ChevronUpIcon } from '@heroicons/react/solid';

const Report = () => {
  const [expandedRacks, setExpandedRacks] = useState({});

  const toggleRack = (rackName) => {
    setExpandedRacks(prev => ({...prev, [rackName]: !prev[rackName]}));
  };

  const handleDelete = (componentId) => {
    // Implement your delete logic here
    console.log(`Delete component with id: ${componentId}`);
  };

  return (
    <div className="bg-gray-100 p-6 rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-6 text-gray-800 border-b pb-2">Component Dashboard</h2>
      
      {/* Filters */}
      <div className="mb-6 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
        <select className="w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-red-500 focus:border-red-500">
          <option value="">All Types</option>
          {/* Add your type options here */}
        </select>
        <select className="w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-red-500 focus:border-red-500">
          <option value="">All Vendors</option>
          {/* Add your vendor options here */}
        </select>
        <select className="w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-red-500 focus:border-red-500">
          <option value="">All Data Centers</option>
          {/* Add your data center options here */}
        </select>
        <input
          type="text"
          placeholder="Search Components"
          className="w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-red-500 focus:border-red-500"
        />
      </div>
      <button className="w-full bg-red-800 text-white p-2 rounded-md hover:bg-red-700 transition-colors duration-150 ease-in-out mb-6">
        Search
      </button>

      {Object.entries(filteredComponents).map(([rackName, rackComponents]) => (
        <div key={rackName} className="mb-4 bg-white rounded-lg shadow-md overflow-hidden">
          <button
            onClick={() => toggleRack(rackName)}
            className="w-full text-left text-lg font-semibold bg-red-800 text-white p-4 flex justify-between items-center hover:bg-red-700 transition-colors duration-150 ease-in-out"
          >
            {rackName}
            {expandedRacks[rackName] ? (
              <ChevronUpIcon className="h-5 w-5" />
            ) : (
              <ChevronDownIcon className="h-5 w-5" />
            )}
          </button>
          {expandedRacks[rackName] && (
            <ul className="divide-y divide-gray-200">
              {[...rackComponents].sort((a, b) => a.startSlot - b.startSlot).map(component => (
                <li key={component._id} className="p-4 hover:bg-gray-50 transition-colors duration-150 ease-in-out">
                  <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center space-y-2 sm:space-y-0">
                    <div className="flex-1">
                      <span className="font-medium text-gray-800">{component.name}</span>
                      <div className="text-sm text-gray-600 mt-1">
                        <span className="inline-block bg-blue-100 text-blue-800 rounded-full px-2 py-1 text-xs font-semibold mr-2">
                          {componentTypes[component.type] || component.type}
                        </span>
                        <span className="mr-2">Slot: {component.startSlot}</span>
                        <span>Slots Occupied: {component.size}</span>
                      </div>
                    </div>
                    <div className="flex">
                      <button
                        onClick={() => handleDelete(component._id)}
                        className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-md text-sm font-medium transition-colors duration-150 ease-in-out focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-opacity-50"
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      ))}
    </div>
  );
};

export default Report;
