import React from 'react'
import { NavLink, Outlet } from 'react-router-dom'

const Home = () => {
  return (
    <>
      <div className="w-full flex flex-col">
  <nav className="fixed w-full z-10">
    <ul className="grid grid-cols-4 shadow-md">
      {[
        { to: '', label: 'G&G DATA CENTER, WOB' },
        { to: 'vrc', label: 'VRC, WOB' },
        { to: 'dr', label: 'DR CENTER, WOB' },
        { to: 'spic', label: 'SPIC, WOB' },
      ].map(({ to, label }) => (
        <li key={to}>
          <NavLink
            to={to}
            className={({ isActive }) =>
              `flex items-center justify-center h-14 transition-colors duration-300 ${
                isActive
                  ? 'bg-amber-500 text-white'
                  : 'bg-white text-gray-700 hover:bg-gray-100'
              }`
            }
          >
            {label}
          </NavLink>
        </li>
      ))}
    </ul>
  </nav>
  
  <main className="pt-20">
    <Outlet />
  </main>
</div>
    </>
  )
}

export default Home
